OnFormLoaded(bDataReloaded)
{
	if(IsObjectDefined("app._xmlSearchForms")==false)return;

	//-- set search opion to all must match and limit to 100
	_eva(tb_limit,100)
	_eva(lb_option,1);

	var bResolveMode = (_swdoc.GetArg("resolvemode")=="1")?true:false;
	_evi(btn_select , bResolveMode);
	_ete(btn_cancel , (bResolveMode)?"Cancel":"Close");

	//-- get picklist details from xml
	if(app._xmlSearchForms==null)
	{
		MessageBox("The search form definition ("+ top._primary_key +") does not exist. Please contact your Administrator");
		_swdoc.CloseForm();
		return;
	}

	var xmlForm = null;
	var arrSearchForms = app._xmlSearchForms.getElementsByTagName('sqlTbForm');
	for(var x=0;x < arrSearchForms.length;x++)
	{
		var strName = app.xmlNodeTextByTag(arrSearchForms[x],"name");
		if(strName==top._primary_key)
		{
			xmlForm = arrSearchForms[x];
			break;
		}
	}

	//-- set sqllist (sl_picklist)
	if(xmlForm!=null)
	{
		//-- 
		_swdoc.SetTitle(app.xmlNodeTextByTag(xmlForm,"title"));

		//-- set sqltable properties
		sl_picklist.table = app.xmlNodeTextByTag(xmlForm,"table");
		_swdoc._selected_treeformtable = sl_picklist.table;
		//sl_picklist.editRecordForm = app.xmlNodeTextByTag(xmlPL,"propertiesForm");
		//sl_picklist.addRecordForm = app.xmlNodeTextByTag(xmlPL,"newRecordForm");	

		//-- get columns and draw out
		var arrColumns = new Array();
		var arrXmlColumns = xmlForm.getElementsByTagName("resultColumn");
		for(var x=0;x<arrXmlColumns.length;x++)
		{
			arrColumns[arrColumns.length++] = app.xmlText(arrXmlColumns[x]);
		}
		sl_picklist._create_bespoke_xmlcols(arrColumns);		


		//-- get search areas and add tables to innerhtml
		var eDivOne = srch_fields_one.element;

		eDivOne.style.overflow='auto';
		var eDivTwo = srch_fields_two.element;
		eDivTwo.style.overflow='auto';

		//-- sf one fields
		var strTableHtml = "<table border='0' cellspacing='0' cellpadding='0' width='100%'>";
		var arrSearchColumns= xmlForm.getElementsByTagName("col1SearchField");	
		for(var x=0;x<arrSearchColumns.length;x++)
		{
			var strBinding = sl_picklist.table + "." + app.xmlText(arrSearchColumns[x]);

			strTableHtml += "<tr>";	

				//-- label
				strTableHtml += "<td class='sf-label' width='50%'>";	
				strTableHtml += "<div class='sf-label-div'>" + app._application_labels[strBinding] + "</div>";	
				strTableHtml += "</td>";	

				//-- input
				strTableHtml += "<td class='sf-input' width='50%'>";
				strTableHtml += "<input type='text' class='sf-input-box' binding='"+strBinding+"'>";
				strTableHtml += "</td>";	

			strTableHtml += "</tr>";	

		}
		strTableHtml += "</table>";	
		eDivOne.innerHTML = strTableHtml;

		//-- sf two fields
		var strTableHtml = "<table border='0' cellspacing='0' cellpadding='0' width='100%'>";
		var arrSearchColumns = xmlForm.getElementsByTagName("col2SearchField");	
		for(var x=0;x<arrSearchColumns.length;x++)
		{
			var strBinding = sl_picklist.table + "." + app.xmlText(arrSearchColumns[x]);

			strTableHtml += "<tr>";	

				//-- label
				strTableHtml += "<td class='sf-label' width='50%'>";	
				strTableHtml += "<div class='sf-label-div'>" + app._application_labels[strBinding] + "</div>";	
				strTableHtml += "</td>";	

				//-- input
				strTableHtml += "<td class='sf-input' width='50%'>";
				strTableHtml += "<input type='text' class='sf-input-box' binding='"+strBinding+"'>";
				strTableHtml += "</td>";	

			strTableHtml += "</tr>";	

		}
		strTableHtml += "</table>";	
		eDivTwo.innerHTML = strTableHtml;

	}

}

