btnFind_OnPressed()
{
	var strFilter = "";
	var strOp = (lb_option.value==1)? " AND ": " OR ";

	var arrInputs = _formdoc.getElementsByTagName("INPUT");
	for(var x=0;x<arrInputs.length;x++)
	{
		if(arrInputs[x].className=='sf-input-box')
		{
			var oEle = arrInputs[x];
			var strBinding = oEle.getAttribute("binding")
			var arrBind = strBinding.split(".");

			if(oEle.value!="")
			{
				var strValue = oEle.value;

				if(strFilter!="") strFilter += strOp;

				if(dd.tables[arrBind[0]].columns[arrBind[1]].IsNumeric())
				{

					if(isNaN(strValue))
					{
						MessageBox("The search field ["+ dd.tables[arrBind[0]].columns[arrBind[1]].DisplayName +"] expects a numeric value. Please re-ener your search criteria");
						return false;
					}
					strFilter+= strBinding + "=" + strValue
				}
				else
				{
					strValue = "'" + app.pfs(strValue) + "%'";
					if(app._dbcaseinsensitive)
					{
						strFilter+= "UPPER(" + arrBind[1].toUpperCase() + ")" + " like " + "UPPER("+strValue+")";
					}
					else
					{
						strFilter+= arrBind[1].toUpperCase() + " like " + strValue
					}
				}
			}//- -ele value != ""
		}
	}//-- for arr inputs

	_een(btn_select,false);
	_een(btn_prop , false);
	_een(btn_delete,false);
	sl_picklist._limit = tb_limit.value;
	_slf(sl_picklist,strFilter);

}

