btn_delete_OnPressed()
{
     //-- nwj :- use key col name instead of numbering

    var sCol = dd.tables[sl_picklist.table].PrimaryKey;
    if(sCol=="")
    {
        MessageBox("No primary key is defined for this table. Please contact your Supportworks Administrator");
    }
    else
    {
        if(sl_picklist.curSel != -1)
        {
            //--  ask them to confirm
            if(!confirm('Are you sure you want to delete the selected record?'))
            return false;

            //-- nwj :- determine if needs quotes
            var strQ = (dd.tables[sl_picklist.table].columns[sCol].IsNumeric())?"":"'";

            var strKeyValue = sl_picklist.GetItemTextRaw(sl_picklist.curSel, sCol);
            if(strKeyValue != "")
            {
                var strSql = "DELETE FROM ";
                strSql += sl_picklist.table;
                strSql += " WHERE ";
                strSql += sCol;
                strSql += " = ";
                strSql += strQ + app.global.PrepareForSQL(strKeyValue) + strQ;
                if(app.global.SqlExecute("swdata", strSql))
                {
   					//-- This next call removes the row from the list, not the database
                    sl_picklist.RemoveRow(sl_picklist.curSel);
                }
            }
        }
    }
}
