method.code:
						OnEnableChangeCallclass()
{
	//-- in app.itsm do not allow them to change the call class
	return false;
}

						OnDataSaved()
{
	//-- 01.05.2008
	//-- if parent window is open refresh tasks list
	if(app.cfg.array_openrfcdocs[opencall.bpm_parentcallref])
	{
		if(app.cfg.array_openrfcdocs[opencall.bpm_parentcallref].document.boolRefreshTasks)
		{
			app.cfg.array_openrfcdocs[opencall.bpm_parentcallref].mainform.sl_tasks.Refresh();
		}
	}

	//-- NWJ - request by pre-sales close form after setting to successfully completed
	if((opencall.bpm_status_id=="Task Successfully Completed")||(opencall.bpm_status_id=="Skip Task (Not Required)")||(opencall.bpm_status_id=="Failed To Complete Task"))
	{
		//app.g.new_dairyevent(opencall.bpm_parentcallref, "The related business process task ("+ opencall.bpm_task_title +") status has been updated to :- " + opencall.bpm_status_id, "Business Process Task", "Status Change");
		_swdoc.CloseForm();
	}
}

						OnDataChanged()
{
	//-- if not resolved or closed then allow them to change status
	app._een(mainform.lb_status,((opencall.status != 6)&&(opencall.status <16)));

	//-- F0101293
	if(app.session.analystId==_swdoc.OC().owner)
	{
		if(_swdoc.OC().action_status!="")
			app._een(_swdoc.MF().btn_actionsts, true);
	}



}

						function OC()
{
	return _swdoc.opencall;
}

function oWF()
{
	return _swdoc.bpm_workflow;
}

function oStage()
{
	return _swdoc.bpm_stage;
}

function MF()
{
	return mainform;
}

						OnFormLoaded()
{
	//-- pad out callref
	app._ete(tb_parentref,app.g.pad_callref(opencall.bpm_parentcallref));

	//-- enable status change only if not completed
	app._een(lb_status,((opencall.status != 6)&&(opencall.status <16)));

	//-- load description
	var boolCache = (_swdoc.OC().status<15);
	app.g.get_call_first_updatetxt(_swdoc.OC().callref, boolCache, _swdoc.MF().tb_updatetxt);

	boolSetStatus = app.session.HaveAppRight(app.PG_BPM, app.BPM_SETSTATUS, app.session.dataDictionary);
	if(!boolSetStatus)
	{
		app._een(lb_status,false);
	}

	//-- NWJ - rel2.1 Part of CR14 - Allow user to skip a task
	if(app.session.HaveAppRight(app.PG_BPM, app.BPM_SKIPTASK))
	{
		var strItems = lb_status.pickList;
		strItems += "Skip Task (Not Required)";
		lb_status.pickList = strItems;
	}
	//-- F0101293
	if(app.session.analystId==_swdoc.OC().owner)
	{
		if(_swdoc.OC().action_status!="")
			app._een(_swdoc.MF().btn_actionsts, true);
	}

}

						lb_status_OnValueChanged(strValue)
{
	//--
	if((strValue.indexOf("Success")!=-1)||(strValue.indexOf("Skip Task")!=-1))
	{
		//-- mark as completed - needed for vpme script
		opencall.bpm_waitingtasks = "0";
	}
	else
	{
		//-- mark as uncompleted
		opencall.bpm_waitingtasks = "1";
	}

}

						popup_call_OnLeftClick()
{
	//-- popup a form 
	if (opencall.bpm_parentcallref!="") app.global.OpenCallDetailsView(opencall.bpm_parentcallref);

	return true;
}

						popup_dataform_OnLeftClick()
{
	//-- open stage or workflow  data form

	//-- get the data table
	var strUseTable  = _swdoc.oWF().ext_db_table


	//-- first make sure there there is a callref rel in this table if not make one
	if(strUseTable=="")
	{
		MessageBox("The data table for the data form has not been linked to the current workflow. It may not operate correctly");
	}
	else
	{
		//-- if we do not have a matching key create a record
		var strPriKeyCol = dd.tables[strUseTable].PrimaryKey;
		if(strPriKeyCol!="")
		{
			//-- does record already exist ??
			var strParams = "tbl="+app.pfu(strUseTable)+"&cr="+_swdoc.OC().callref;
			app.g.submitsqs("call/common/check_bpm_dataform",strParams,false);
		}
		else
		{
			MessageBox("The data table for the data form does not have a primary key field. It may not operate correctly");
		}

	}

	//-- now open the form (use stage form if there is one otherwise use the workflow form).
	var strArgs="";	
	var strForm = _swdoc.oStage().swdataform;
	if(strForm=="")strForm = _swdoc.oWF().ext_sw_form;
	//###20120618 - prevent popup form if not given.
	if (""!=strForm) app.OpenFormForEdit(strForm, opencall.bpm_parentcallref, strArgs, true);

	return true;
}

						btn_actionsts_OnPressed()
{
	// TODO: Add your event handler code here
	var xmlmc = new XmlMethodCall;

	// Set values for the Input parameters - Parameter values must be set in the same order they are expected by the method.
	xmlmc.SetParam("callref", _swdoc.OC().callref); 				

	// Invoke the VPME method LogCall.
	if(xmlmc.Invoke("VPME", "clearActionStatus"))
	{
    	// Success, lets access output parameters
    	app._een(_swdoc.MF().btn_actionsts, false);
	}
	else
	{
    	// Error
    	MessageBox(xmlmc.GetLastError());
	}


}

						btn_copy_OnPressed()
{
	// TODO: Add your event handler code here

		var strURL = "task=" + app.pfu(_swdoc.OC().bpm_task_title) + "&desc=" + app.pfu(_swdoc.OC().prob_text) + "&supgroup=" + app.pfu(_swdoc.OC().suppgroup)
          + "&analyst=" + app.pfu(_swdoc.OC().owner) + "&priority=" + app.pfu(_swdoc.OC().priority) + "&copy=true"
		  + "&flg_seq=0&fk_callref=" + app.pfu(_swdoc.OC().bpm_parentcallref) + "&fk_stage_id=" + app.pfu(_swdoc.OC().bpm_stage_id)
		  + "&fk_workflow_id=" + app.pfu(_swdoc.OC().bpm_workflow_id) + "&stage_title=" + app.pfu(_swdoc.OC().bpm_stage_title)
		  + "&thirdpartyref="+app.pfu(_swdoc.OC().third_party_reference)+ "&thirdpartycontract="+app.pfu(_swdoc.OC().tpcontract);
		app.g.popup("bpm_add_octask",strURL);
}
