//-- based on the current workflow stage show / hide tabs / buttons etc
function setup_process_fields(boolDocChange)
{
	//-- F0105032
	if(_swdoc.OC().bpm_workflow_id!="")
	{
		if(_swdoc.strCurrentWorkflow != _swdoc.OC().bpm_workflow_id)
		{
			_swdoc.arrAudit = new Array();
			var strParams = "fk_workflow_id="+app.pfu(bpm_workflow.pk_workflow_id);
   			var oRS = app.g.get_sqrecordset("call/common/get_audit_fields",strParams);
			while(oRS.Fetch())
			{
				var strBinding = app.g.get_field(oRS,"fieldname");
				arrBinding = strBinding.split(".");
				_swdoc.arrAudit[strBinding] = _swdoc[arrBinding[0]][arrBinding[1]];
			}
			_swdoc.strCurrentWorkflow = _swdoc.OC().bpm_workflow_id;
		}
	}//-- F0105032

	//--
	//-- check if we need to initialise stage (in case something has gone wrong somwhere in vpme)
	if((_swdoc.OC().bpm_workflow_id!="")&&(_swdoc.OC().bpm_stage_id < 1))
	{
		var oRec = app.g.get_record("bpm_workflow", _swdoc.OC().bpm_workflow_id);
		if(oRec)
		{
			var strStageID = oRec.fk_firststage_id;
			_swdoc.ChangeRecord("bpm_stage", strStageID)			
		}	
	}

	//-- if user can add tasks and this stage allows it
	var boolActive = false;
	if(_swdoc.OC().status<16)
		boolActive = true;
	//-- if user can add tasks and this stage allows it
	var strParams = "cr="+app.pfu(_swdoc.OC().callref)+"&sid="+_swdoc.oStage().pk_stage_id;
	var intTaskCount = app.g.sqs_rowcount("call/common/cnt_bp_tasks",strParams);

    var boolHasTasks = intTaskCount>0;
   	var boolAddTasks = ( (_swdoc.oStage().flg_addcalltasks==1) &&(boolActive)&&(app.session.HaveAppRight(app.PG_BPM, app.BPM_ADDTASK,app.session.dataDictionary)) )
	var boolShowTasks = ((_swdoc.oStage().flg_hastasks==1)||(boolAddTasks)||(boolHasTasks));
	//-- F0090013 only show tasks tab if there are tasks or can add on fly - as stage may have tasks, but they may not be created
	var boolShowTasks = ((boolAddTasks)||(boolHasTasks));

	app._een(_swdoc.MF().mbtn_mod_task,boolAddTasks);
	_swdoc.MF().TabDet.ShowTabItem(2,boolShowTasks);
	if((boolShowTasks)&&(boolDocChange))
	{
		//-- NWJ - let SW handle refreshing ...
		//MF().sl_tasks.Refresh();
	}
	if(_swdoc.oStage().flg_unanimous_auth==1)
	{
		app._evi(_swdoc.MF().authWeighting,false);
	}	

	//-- AUTH STUFF
	var boolRequiresAuth = (_swdoc.OC().bpm_waitingauth==1);	
	var boolAddAuths = ( (_swdoc.oStage().flg_addcallauths==1) && (app.session.HaveAppRight(app.PG_BPM, app.BPM_ADDAUTH,app.session.dataDictionary)) )

	//-- if we have auth show tab and disable status lb
	_swdoc.MF().TabDet.ShowTabItem(1, boolRequiresAuth||boolAddAuths);
	//--only disable manual progression if stage is marked to disable AND stage has tasks (or requires auth)
	var boolTaskStop = _swdoc.oStage().flg_disable_manual==1 && boolHasTasks;
	app._een(_swdoc.MF().lb_wf_state,(!(boolTaskStop|| boolRequiresAuth)));

	app._evi(_swdoc.MF().mbtn_mod_auth,boolAddAuths &&(boolActive));	
	//-- EOF AUTH STUFF

	//-- if we have data form show button
	app._een(_swdoc.MF().btn_bpm_form,((_swdoc.oWF().ext_sw_form!="")||(_swdoc.oStage().swdataform!="")));

	//-- select tab 0 or 1 depending on auth
	app._etab(_swdoc.MF().TabDet,(boolRequiresAuth)?1:0);

	if(_swdoc.OC().bpm_workflow_id!="")
	{
		//-- whether we enable the progress button, only do so if there is a progress associated with the bpm process
		var intCount = app.g.sqs_rowcount("call/common/cnt_progress_items","wf="+app.pfu(_swdoc.OC().bpm_workflow_id));
		if(intCount>0)
			app._een(_swdoc.MF().btn_bpm_progress,true);
	}
	if(boolDocChange)
			app.g.reset_bpm_form_attribute_check(_swdoc);		
	app.g.process_bpm_form_attribute_check(_swdoc, _swdoc.OC().bpm_workflow_id, _swdoc.oStage().pk_stage_id);	
	if(boolRequiresAuth)
	{
		app._slf(_swdoc.MF().sl_wfauth, "fk_callref = " + _swdoc.OC().callref + " and fk_stage_id = " + _swdoc.OC().bpm_stage_id);
	}
	return true;
}


//-- given an analyst object (from app.global.js) load values for either monitored / raised by analyst
//-- for the rfc
function set_rfc_analyst(oAnalyst,boolMonitoredBy)
{
	if(!oAnalyst) return false;

	if(boolMonitoredBy)
	{
		_swdoc.RFC_EXT().monitored_by_analystid = oAnalyst.id;
		_swdoc.RFC_EXT().monitored_by_name = oAnalyst.name;
		_swdoc.RFC_EXT().monitored_by_group = oAnalyst.supportgroup;
		_swdoc.RFC_EXT().monitored_by_telephone =  oAnalyst.telephone;
		_swdoc.RFC_EXT().monitored_by_email = oAnalyst.email;	
	}
	else
	{
		_swdoc.RFC_EXT().raised_by_analystid = oAnalyst.id;
		_swdoc.RFC_EXT().raised_by_name = oAnalyst.name;
		_swdoc.RFC_EXT().raised_by_group = oAnalyst.supportgroup;
		_swdoc.RFC_EXT().raised_by_telephone =  oAnalyst.telephone;
		_swdoc.RFC_EXT().raised_by_email = oAnalyst.email;
	}
	_swdoc.UpdateFormFromData();	
}

//--
//-- filter customer impact
function filter_customer_impact()
{
	//-- get list of customers impacted by these incidents and refresh cust impact list
	_swdoc.EF().sl_incs1.Refresh();
	var strCustFilter = app.g.sl_get_valuesdel(_swdoc.EF().sl_incs1,"cust_id","','","",true);
	app._slf(_swdoc.EF().sl_customerimpact,"_kv=" + strCustFilter);
	_swdoc.EF().sl_customerimpact.Refresh();
}


//--
//-- FUNCTIONS TO ASSOCIATE CALL RECORDS AND CIS TO RELEASE

//--
//-- given a list of incident refs link them to release
function associate_incidents_to_release(strIncRefs)
{
	//-- create call diary
	var array_incs = strIncRefs.split(",");  
	var strText = "This release request has been associated with the following incidents\n\n";
	var boolINCs = false;
	//-- loop through incidents and link them
    for (var x=0;x<array_incs.length;x++)
    {
        var inc_callref = array_incs[x];
        var inc_callref_text = app.g.pad_callref(inc_callref);

		//-- check that incident is not already in the list
		rowPos = app.g.sqllist_findrow_byvalue(_swdoc.EF().sl_incs1,inc_callref,"callref");
		if (rowPos == -1)
		{ 		
			boolINCs=true;
			//-- call app.g method to link incident to release
			if(app.g.relate_calls(_swdoc.OC().callref,inc_callref,'RELEASE-INCIDENT'))
			{
				strText += "Incident (" + inc_callref_text + ") associated\n";

				//-- update known error diary
				var strIncText = "The release Request (" + app.g.pad_callref(_swdoc.OC().callref) + ") has been associated to this incident.";
				app.g.new_diaryevent(inc_callref,strIncText,"Release Management","Release Association");
			}
		}
    } //-- eof for

	//-- create change call diary update then reload list
	if (boolINCs)
	{
		app.g.new_diaryevent(_swdoc.OC().callref,strText,"Release Management","Incident Association");
		_swdoc.EF().sl_incs1.Refresh();
		_swdoc.filter_customer_impact()		
	}	

}

//--
//-- given a list of incident refs link them to release
function associate_cause_incidents_to_release(strIncRefs)
{
	//-- create call diary
	var array_incs = strIncRefs.split(",");  
	var strText = "This release request has been associated as the cause of the following incidents\n\n";
	var boolINCs = false;
	//-- loop through incidents and link them
    for (var x=0;x<array_incs.length;x++)
    {
        var inc_callref = array_incs[x];
        var inc_callref_text = app.g.pad_callref(inc_callref);

		//-- check that incident is not already in the list
		rowPos = app.g.sqllist_findrow_byvalue(_swdoc.EF().sl_causeinc1,inc_callref,"callref");
		if (rowPos == -1)
		{ 		
			boolINCs=true;
			//-- call app.g method to link incident to release
			if(app.g.relate_calls(_swdoc.OC().callref,inc_callref,'RELEASE-CINCIDENT'))
			{
				strText += "Incident (" + inc_callref_text + ") associated\n";

				//-- update known error diary
				var strIncText = "The release Request (" + app.g.pad_callref(_swdoc.OC().callref) + ") has been associated as a cause of this incident.";
				app.g.new_diaryevent(inc_callref,strIncText,"Release Management","Release Association");
			}
		}
    } //-- eof for

	//-- create change call diary update then reload list
	if (boolINCs)
	{
		app.g.new_diaryevent(_swdoc.OC().callref,strText,"Release Management","Incident Association");
		_swdoc.EF().sl_causeinc1.Refresh();
		_swdoc.filter_customer_impact()		
	}	

}
//--
//-- given a list of error refs link them to release
function associate_errors_to_release(strErrorRefs)
{
	//-- create call diary
	var array_kes = strErrorRefs.split(",");  
	var strText = "This release request has been associated with the following known errors\n\n";
	var boolKEs = false;
	//-- loop through kes and link them
    for (var x=0;x<array_kes.length;x++)
    {
        var ke_callref = array_kes[x];
        var ke_callref_text = app.g.pad_callref(ke_callref);

		//-- check that ke is not already in the list
		rowPos = app.g.sqllist_findrow_byvalue(_swdoc.EF().sl_kes1,ke_callref,"callref");
		if (rowPos == -1)
		{ 		
			boolKEs=true;
			//-- call app.g method to link ke to release
			if(app.g.relate_calls(_swdoc.OC().callref,ke_callref,'RELEASE-ERROR'))
			{
				strText += "Known Error (" + ke_callref_text + ") associated\n";

				//-- update known error diary
				var strKeText = "The release Request (" + app.g.pad_callref(_swdoc.OC().callref) + ") has been associated to this known error.";
				app.g.new_diaryevent(ke_callref,strKeText,"Release Management","Release Association");
			}
		}
    } //-- eof for

	//-- create change call diary update then reload list
	if (boolKEs)
	{
		app.g.new_diaryevent(_swdoc.OC().callref,strText,"Release Management","Known Error Association");
		_swdoc.EF().sl_kes1.Refresh();


		//-- check if we should add ke incidents to the release
		if(dd.GetGlobalParamAsNumber("Application Settings/Release/CopyErrorIncidents")==1)
		{
			//-- get error incident records
			var strLinkIncidents = app.g.get_slave_calls(strErrorRefs,"PROBLEM-INCIDENT");
			if(strLinkIncidents!="0")
			{
 				_swdoc.associate_incidents_to_release(strLinkIncidents);
			}
		}

		//-- Now check if we should link ke cause ci items to the release
		if(dd.GetGlobalParamAsNumber("Application Settings/Release/CopyErrorCauseCIs")==1)
		{
			//-- if dont have info get it otherwise use info we go above
			var oRes = app.cmdb.get_call_ci_info(strErrorRefs , "PROBLEM-CAUSE", "");
			if(oRes.keys!="")_swdoc.associate_cis_to_release(oRes.keys,oRes.text);
		}
	}
}

//--
//-- given a list of rfc refs link them to release
function associate_rfcs_to_release(strRFCRefs)
{
	var array_rels = strRFCRefs.split(",");  
	var strText = "This release request has been associated with the following change requests\n\n";
	var boolRELs = false;
	//-- loop through selected problems and link them
    for (var x=0;x<array_rels.length;x++)
    {
        var rfc_callref = array_rels[x];
        var rfc_callref_text = app.g.pad_callref(rfc_callref);

		//-- check that incident is not already in the list
		rowPos = app.g.sqllist_findrow_byvalue(_swdoc.EF().sl_rels1,rfc_callref,"callref");
		if (rowPos == -1)
		{ 		
			boolRELs=true;
			//-- call app.g method to link problem to incident
			if(app.g.relate_calls(_swdoc.OC().callref,rfc_callref,'RELEASE-RFC'))
			{
				strText += "Change Request (" + rfc_callref_text + ") associated\n";

				//-- update rfc diary
				strRfcText = "The release request (" + app.g.pad_callref(_swdoc.OC().callref) + ") has been associated to this change.";
				app.g.new_diaryevent(rfc_callref,strRfcText,"Release Management","Change Association");
			}
		}
    } //-- eof for

	//-- create incident call diary update then reload list
	if (boolRELs)
	{
		app.g.new_diaryevent(_swdoc.OC().callref,strText,"Release Management","Release Association");
		_swdoc.EF().sl_rels1.Refresh();

		//-- check if we should add rfc incidents to the release
		if(dd.GetGlobalParamAsNumber("Application Settings/Release/CopyRFCIncidents")==1)
		{
			//-- get rfc incident records
			var strLinkIncidents = app.g.get_slave_calls(strRFCRefs,"RFC-INCIDENT");
			if(strLinkIncidents!="0")
			{
 				_swdoc.associate_incidents_to_release(strLinkIncidents);
			}
		}

		//-- check if we should add rfc incidents to the release
		if(dd.GetGlobalParamAsNumber("Application Settings/Release/CopyRFCErrors")==1)
		{
			//-- get rfc error records
			var strLinkErrors = app.g.get_slave_calls(strRFCRefs,"RFC-ERROR");
			if(strLinkErrors!="0")
			{
 				_swdoc.associate_errors_to_release(strLinkErrors);
			}
		}

		//-- Now check if we should link ke cause ci items to the release
		if(dd.GetGlobalParamAsNumber("Application Settings/Release/CopyRFCChangeCIs")==1)
		{
			//-- if dont have info get it otherwise use info we go above
			var oRes = app.cmdb.get_call_ci_info(strRFCRefs , "RFC-CAUSE", "");
			if(oRes.keys!="")_swdoc.associate_cis_to_release(oRes.keys,oRes.text);
		}
	}
}

//--
//-- given a list of ci item ids link them the to release items
function associate_cis_to_release(strCiIDs, strCiTexts, strCiTypes)
{
	//-- loop through selected CIs
	var strText = "";
	var strServiceText = "";
	var arrayCIkeys = strCiIDs.split(",");
	var arrayCItext = strCiTexts.split(",");

	// -- SG, 03/07/2012, F0103324, required variable and array creation
	var itemType = "Configuration Item";
	var countBespokeTerms = 0;
	bespokeTermsArray = new Array();	

	if(	strCiTypes!=undefined)
	{
		var arrayCItypes = strCiTypes.split(",");
		for (var x=0;x<arrayCIkeys.length;x++)
    	{
        	var pk_ci_id = arrayCIkeys[x];
        	var ci_item_text = arrayCItext[x];
        	var ci_type_text = arrayCItypes[x];

			// Build array of CI's that have the Bespoke Terms field populated
			var rec = app.g.get_record("config_itemi", pk_ci_id);
			if(rec.recordresolvedmsg != '')
			{
    			bespokeTermsArray[countBespokeTerms] = new Array(2);
    			bespokeTermsArray[countBespokeTerms][0] = rec.ck_config_item;
    			bespokeTermsArray[countBespokeTerms][1] = rec.recordresolvedmsg;
    			countBespokeTerms++;
			}
			// End of array building

			//-- check that CI is not already in the list
			if(ci_type_text.indexOf("Service")>-1)
  			{
 				//-- check that CI is not already in the list
				rowPos = app.g.sqllist_findrow_byvalue(_swdoc.EF().sl_services,pk_ci_id,0);
				if (rowPos == -1)
				{ 
					//-- Relate the CI to the call
					if (app.cmdb.relate_ci_to_call(_swdoc.OC().callref,pk_ci_id,'RELEASE'))
					{
						if(strServiceText!="")strServiceText += ",";
						strServiceText += ci_item_text;
					}
				}
  			}else
  			{
				rowPos = app.g.sqllist_findrow_byvalue(_swdoc.EF().sl_configitems1,pk_ci_id,'pk_auto_id');
				if (rowPos == -1)
				{ 
					//-- Relate the CI to the call
					if (app.cmdb.relate_ci_to_call(_swdoc.OC().callref,pk_ci_id,'RELEASE'))
					{
						if(strText != "") strText +=",";
						strText += ci_item_text;
					}
				}
			}
		}  

		//-- apply call diary update for added CIs (if we have any to add)
		if (strServiceText!="")
		{
			strServiceText = "The following services, for change, were added to this change request.\n" + strServiceText;
			var strActionSource = "Change Management";
			var strCode			= "Linking Services";

			app.g.new_dairyevent(_swdoc.OC().callref,strServiceText,strActionSource,strCode);
		}

	}
	else{
		for (var x=0;x<arrayCIkeys.length;x++)
    	{
        	var pk_ci_id = arrayCIkeys[x];
        	var ci_item_text = arrayCItext[x];

			//-- check that CI is not already in the list
			rowPos = app.g.sqllist_findrow_byvalue(_swdoc.EF().sl_configitems1,pk_ci_id,"pk_auto_id");
			if (rowPos == -1)
			{ 
				// Build array of CI's that have the Bespoke Terms field populated
				var rec = app.g.get_record("config_itemi", pk_ci_id);
				if(rec.recordresolvedmsg != '')
				{
    				bespokeTermsArray[countBespokeTerms] = new Array(2);
    				bespokeTermsArray[countBespokeTerms][0] = rec.ck_config_item;
    				bespokeTermsArray[countBespokeTerms][1] = rec.recordresolvedmsg;
    				countBespokeTerms++;
				}
				// End of array building

				//-- Relate the CI to the call
				if (app.cmdb.relate_ci_to_call(_swdoc.OC().callref,pk_ci_id,'RELEASE'))
				{
					if(strText != "") strText +=",";
					strText += ci_item_text;
				}
			}
		}  
	} 

	//-- apply call diary update for added CIs (if we have any to add)
	if (strText!="")
	{
		strText = "The following configuration items, for release, were added to this release request.\n" + strText;
		var strActionSource = "Release Management";
		var strCode			= "Associated Configuration Items";

		app.g.new_dairyevent(_swdoc.OC().callref,strText,strActionSource,strCode);
	}

	_swdoc.EF().sl_configitems1.Refresh();
	_swdoc.EF().sl_services.Refresh();

	// -- SG, 03/07/2012, F0103324
	app.bespokeTermsOutput(bespokeTermsArray, arrayCIkeys.length, itemType);
}

//--
//-- given a list of ci item ids link them the to release items
function associate_services_to_release(strCiIDs, strCiTexts)
{
	//-- loop through selected CIs
	var strText = "";
	var arrayCIkeys = strCiIDs.split(",");
	var arrayCItext = strCiTexts.split(",");	
	// -- SG, 03/07/2012, F0103324, required variable and array creation
	var itemType = "Configuration Item";
	var countBespokeTerms = 0;
	bespokeTermsArray = new Array();	

	for (var x=0;x<arrayCIkeys.length;x++)
    {
        var pk_ci_id = arrayCIkeys[x];
        var ci_item_text = arrayCItext[x];

		//-- check that CI is not already in the list
		rowPos = app.g.sqllist_findrow_byvalue(_swdoc.EF().sl_services,pk_ci_id,"pk_auto_id");
		if (rowPos == -1)
		{ 
			// Build array of CI's that have the Bespoke Terms field populated
			var rec = app.g.get_record("config_itemi", pk_ci_id);
			if(rec.recordresolvedmsg != '')
			{
    			bespokeTermsArray[countBespokeTerms] = new Array(2);
    			bespokeTermsArray[countBespokeTerms][0] = rec.ck_config_item;
    			bespokeTermsArray[countBespokeTerms][1] = rec.recordresolvedmsg;
    			countBespokeTerms++;
			}
			// End of array building

			//-- Relate the CI to the call
			if (app.cmdb.relate_ci_to_call(_swdoc.OC().callref,pk_ci_id,'RELEASE'))
			{
				if(strText != "") strText +=",";
				strText += ci_item_text;
			}
		}
	}   

	//-- apply call diary update for added CIs (if we have any to add)
	if (strText!="")
	{
		strText = "The following services, for release, were added to this release request.\n" + strText;
		var strActionSource = "Release Management";
		var strCode			= "Associated Services";

		app.g.new_dairyevent(_swdoc.OC().callref,strText,strActionSource,strCode);
		_swdoc.EF().sl_services.Refresh();
	}
	// -- SG, 03/07/2012, F0103324
	app.bespokeTermsOutput(bespokeTermsArray, arrayCIkeys.length, itemType);
}
//--
//-- given a list of rfc refs link them to release
function associate_srs_to_release(strSRRefs)
{
	var array_rels = strSRRefs.split(",");  
	var strText = "This release request has been associated with the following service requests\n\n";
	var boolSRs = false;
	//-- loop through selected problems and link them
    for (var x=0;x<array_rels.length;x++)
    {
        var sr_callref = array_rels[x];
        var sr_callref_text = app.g.pad_callref(sr_callref);

		//-- check that incident is not already in the list
		rowPos = app.g.sqllist_findrow_byvalue(_swdoc.EF().sl_srs,sr_callref,"callref");
		if (rowPos == -1)
		{ 		
			boolSRs=true;
			//-- call app.g method to link problem to incident
			if(app.g.relate_calls(_swdoc.OC().callref,sr_callref,'RELEASE-REQUEST'))
			{
				strText += "Service Request (" + sr_callref_text + ") associated\n";

				//-- update rfc diary
				strRfcText = "The release request (" + app.g.pad_callref(_swdoc.OC().callref) + ") has been associated to this service request.";
				app.g.new_diaryevent(sr_callref,strRfcText,"Release Management","Service Request Association");
			}
		}
    } //-- eof for

	//-- create incident call diary update then reload list
	if (boolSRs)
	{
		app.g.new_diaryevent(_swdoc.OC().callref,strText,"Release Management","Release Association");
		_swdoc.EF().sl_srs.Refresh();
	}
}

function link_cause_problems_to_change(strSelectedProbs)
{
	var array_probs = strSelectedProbs.split(",");  
	var strText = "This release request has been associated as the cause of the following problems\n\n";
	var boolPROBs = false;
	//-- loop through selected problems and link them
    for (var x=0;x<array_probs.length;x++)
    {
        var prob_callref = array_probs[x];
        var prob_callref_text = app.g.pad_callref(prob_callref);

		//-- check that incident is not already in the list
		rowPos = app.g.sqllist_findrow_byvalue(_swdoc.EF().sl_causeprob1,prob_callref,0);
		if (rowPos == -1)
		{ 		
			boolPROBs=true;
			//-- call app.g method to link problem to incident
			if(app.g.relate_calls(_swdoc.OC().callref,prob_callref,'RELEASE-CPROBLEM'))
			{
				strText += "Problem (" + prob_callref_text + ") associated\n";

				//-- update known error diary
				strProbText = "The Release Request (" + app.g.pad_callref(_swdoc.OC().callref) + ") has been associated as a cause of this problem.";
				app.g.new_diaryevent(prob_callref,strProbText,"Release Management","Release Association");
			}
		}
    } //-- eof for

	//-- create change call diary update then reload list
	if (boolPROBs)
	{
		app.g.new_diaryevent(_swdoc.OC().callref,strText,"Release Management","Release Association");
		_swdoc.EF().sl_causeprob1.Refresh();
	}
}

//-- FUNCTIONS TO REMOVE CALL AND CI ASSOCIATION FROM RELEASE


//-- function to remove related release cis
function remove_release_cis(strRemoveCis, strRemoveCisText)
{
	strRemoveCis = _swdoc.get_existingvalues(strRemoveCis,"pk_auto_id", _swdoc.EF().sl_configitems1);
	if(strRemoveCis=="")return;


    //-- NWJ - 28.02.2006 - Use CMDB object to remove CIs from call
    if (app.cmdb.unrelate_ci_from_call(_swdoc.OC().callref,strRemoveCis,'RELEASE'))
    {
    	//-- update incident diary
    	var strText     	= "The following configuration items were removed from this release request.\n" + strRemoveCisText;
		var strActionSource = "Release Management";
		var strCode			= "Remove Configuration Items";
		app.g.new_dairyevent(_swdoc.OC().callref,strText,strActionSource,strCode);
    }			

	//-- re-filter list	
	_swdoc.EF().sl_configitems1.Refresh();
}

//-- function to remove related release cis
function remove_release_services(strRemoveCis, strRemoveCisText)
{
	strRemoveCis = _swdoc.get_existingvalues(strRemoveCis,"pk_auto_id", _swdoc.EF().sl_services);
	if(strRemoveCis=="")return;


    //-- NWJ - 28.02.2006 - Use CMDB object to remove CIs from call
    if (app.cmdb.unrelate_ci_from_call(_swdoc.OC().callref,strRemoveCis,'RELEASE'))
    {
    	//-- update incident diary
    	var strText     	= "The following services were removed from this release request.\n" + strRemoveCisText;
		var strActionSource = "Release Management";
		var strCode			= "Remove Services";
		app.g.new_dairyevent(_swdoc.OC().callref,strText,strActionSource,strCode);
    }			

	//-- re-filter list	
	_swdoc.EF().sl_services.Refresh();
}

//-- remove incidents from release
function remove_release_incidents(strRemoveRefs)
{
	strRemoveRefs = _swdoc.get_existingvalues(strRemoveRefs,"callref", _swdoc.EF().sl_incs1);
	if(strRemoveRefs=="")return;

	if(app.g.unrelate_slave_calls(_swdoc.OC().callref,strRemoveRefs,'RELEASE-INCIDENT'))
	{
		var arrCallrefs = strRemoveRefs.split(',');
		var strText = "This release request has been dis-associated from the following incidents\n\n";

		for(var x=0;x<arrCallrefs.length;x++)
		{
			strText += "Incident Request (" + app.g.callref_pad(arrCallrefs[x]) + ") dis-associated\n";

			var strKEText = "The release request (" + app.g.callref_pad(_swdoc.OC().callref) + ") has been dis-associated from this incident request";
			app.g.new_diaryevent(arrCallrefs[x], strKEText,"Release Management","Release Dis-association");

		}
		app.g.new_diaryevent(_swdoc.OC().callref,strText,"Release Management","Incident Dis-association");
		_swdoc.EF().sl_incs1.Refresh();
	}

	_swdoc.filter_customer_impact();
}

//-- remove errors from release
function remove_release_errors(strRemoveRefs)
{
	strRemoveRefs = _swdoc.get_existingvalues(strRemoveRefs,"callref", _swdoc.EF().sl_kes1);
	if(strRemoveRefs=="")return;

	if(app.g.unrelate_slave_calls(_swdoc.OC().callref,strRemoveRefs,'RELEASE-ERROR'))
	{
		var arrCallrefs = strRemoveRefs.split(',');
		var strText = "This release request has been dis-associated from the following known errors\n\n";

		for(var x=0;x<arrCallrefs.length;x++)
		{
			strText += "Known Error Request (" + app.g.callref_pad(arrCallrefs[x]) + ") dis-associated\n";

			var strKEText = "The release request (" + app.g.callref_pad(_swdoc.OC().callref) + ") has been dis-associated from this known error request";
			app.g.new_diaryevent(arrCallrefs[x], strKEText,"Release Management","Release Dis-association");

		}
		app.g.new_diaryevent(_swdoc.OC().callref,strText,"Release Management","Known Error Dis-association");
		_swdoc.EF().sl_kes1.Refresh();
	}

	//-- if auto assoc incidents then check if we should remove
	if(dd.GetGlobalParamAsNumber("Application Settings/Release/CopyErrorIncidents")==1)
	{
		//--
		//-- get incident refs for the errors we are removing - if any are linked to rfc prompt user to remove them
		var strLinkedINCs = app.g.get_slave_calls(strRemoveRefs , "PROBLEM-INCIDENT", "");
		if(strLinkedINCs!="0")
		{
			if(confirm("There may be associated incident records, that are linked to the known errors you are removing. Do you want to remove these incidents as well?"))
			{
				_swdoc.remove_release_incidents(strLinkedINCs);
			}
		}
	}



	//-- if auto assoc cis check if we should remove
	if(dd.GetGlobalParamAsNumber("Application Settings/Release/CopyErrorCauseCIs")==1)	
	{
		var oRes = app.cmdb.get_call_ci_info(strRemoveRefs , "PROBLEM-CAUSE", "");
		if(oRes.keys!="")
		{
			if(confirm("There may be associated release configuration items, that are linked to the known errors you are removing. Do you want to remove these items as well?"))
			{
				_swdoc.remove_release_cis(oRes.keys,oRes.text);
			}
		}
	}	
}

//- -remove rfcs from release
function remove_release_rfcs(strRemoveRefs)
{
	strRemoveRefs = _swdoc.get_existingvalues(strRemoveRefs,"callref", _swdoc.EF().sl_rels1);
	if(strRemoveRefs=="")return;

	if(app.g.unrelate_slave_calls(_swdoc.OC().callref,strRemoveRefs,'RELEASE-RFC'))
	{
		var arrCallrefs = strRemoveRefs.split(',');
		var strText = "This release request has been dis-associated from the following change requests\n\n";

		for(var x=0;x<arrCallrefs.length;x++)
		{
			strText += "Change Request (" + app.g.callref_pad(arrCallrefs[x]) + ") dis-associated\n";

			var strKEText = "The release request (" + app.g.callref_pad(_swdoc.OC().callref) + ") has been dis-associated from this change request";
			app.g.new_diaryevent(arrCallrefs[x], strKEText,"Release Management","Release Dis-association");

		}
		app.g.new_diaryevent(_swdoc.OC().callref,strText,"Release Management","Change Dis-association");
		_swdoc.EF().sl_rels1.Refresh();
	}


	if(dd.GetGlobalParamAsNumber("Application Settings/Release/CopyRFCIncidents")==1)
	{
		//--
		//-- get incident refs for the rfcs we are removing - if any are linked to rfc prompt user to remove them
		var strLinkedINCs = app.g.get_slave_calls(strRemoveRefs , "RFC-INCIDENT", "");
		if(strLinkedINCs!="0")
		{
			if(confirm("There may be associated incident records, that are linked to the change requests you are removing. Do you want to remove these incidents as well?"))
			{
				_swdoc.remove_release_incidents(strLinkedINCs);
			}
		}
	}

	if(dd.GetGlobalParamAsNumber("Application Settings/Release/CopyRFCErrors")==1)
	{
		//--
		//-- get incident refs for the rfcs we are removing - if any are linked to rfc prompt user to remove them
		var strLinkedErrors = app.g.get_slave_calls(strRemoveRefs , "RFC-ERROR", "");
		if(strLinkedErrors!="0")
		{
			if(confirm("There may be associated known errors records, that are linked to the change requests you are removing. Do you want to remove these known errors as well?"))
			{
				_swdoc.remove_release_errors(strLinkedErrors);
			}
		}
	}	

	//-- if auto assoc cis check if we should remove
	if(dd.GetGlobalParamAsNumber("Application Settings/Release/CopyRFCChangeCIs")==1)	
	{
		var oRes = app.cmdb.get_call_ci_info(strRemoveRefs , "RFC-CAUSE", "");	
		if(oRes.keys!="")
		{
			if(confirm("There may be associated release configuration items, that are linked to the change requests you are removing. Do you want to remove these items as well?"))
			{
				_swdoc.remove_release_cis(oRes.keys,oRes.text);
			}
		}

	}	
}

