						OnFormLoaded()
{
	app._slf(_swdoc.EF().sl_configitems1,"cr="+_swdoc.opencall.callref+"&code=RELEASE&type=1");
	_swdoc.EF().sl_configitems1.Refresh();
	//-- F0105032
	if(_swdoc.OC().bpm_workflow_id!="")
	{
		_swdoc.strCurrentWorkflow = _swdoc.OC().bpm_workflow_id;
		var strParams = "fk_workflow_id="+app.pfu(bpm_workflow.pk_workflow_id);
   		var oRS = app.g.get_sqrecordset("call/common/get_audit_fields",strParams);
		while(oRS.Fetch())
		{
			var strBinding = app.g.get_field(oRS,"fieldname");
			arrBinding = strBinding.split(".");
			_swdoc.arrAudit[strBinding] = _swdoc[arrBinding[0]][arrBinding[1]];
		}
	}//-- EOF F0105032

	//-- if edit only disable status field for workflow
	if (!app.itsm.can_edit(_swdoc.OC().callclass,false))
	{
		app._een(lb_wf_state,false);
	}

	_swdoc.intLastOCStatus = _swdoc.OC().status;
  	_swdoc.setup_process_fields(false);
	_swdoc.strOriginalSLA = _swdoc.opencall.priority;
	_swdoc.strLastBPM_Status = _swdoc.OC().bpm_status_id;
	_swdoc.check_act_warnings();
	_swdoc.setup_release_subtab();
	app._ete(_swdoc.MF().lbl_oc_callref,app.g.callref_pad(_swdoc.OC().callref));

	//-- F0101293
	if(app.session.analystId==_swdoc.OC().owner)
	{
		if(_swdoc.OC().action_status!="")
			app._een(_swdoc.MF().btn_actionsts, true);
	}

	// F0111914
	_swdoc.boolILAReviewEnabled = app.g.rla_releaselogform_enable(); // Risk Level Assessment Enabled
	app._een(_swdoc.MF().btn_risk_review, _swdoc.boolILAReviewEnabled); // enable the Risk Assessment button based on setting

	// DTH: Generate a list of the IDs for the selected Risk Assessment Options
	var strParams = "cr="+_swdoc.OC().callref;
	var oRS = app.g.get_sqrecordset("call/change/get_rla_options",strParams);
	while(oRS.Fetch())
	{
		if (_swdoc.strRiskOptions != "") _swdoc.strRiskOptions += ",";
		_swdoc.strRiskOptions += app.g.pfs(app.g.get_field(oRS,"FK_ILA_OPTION_ID"));
	}
	_swdoc.strOrigRiskOptions = _swdoc.strRiskOptions;
	// F0111914 EOF

	//--F0108045  
	app.g.add_me_document("REQUEST", _swdoc.OC().callref, _swdoc);

	// -- Refresh Authorisation list
	_swdoc.MF().sl_wfauth.Refresh();

}

