						OnTimer(nTimer)
{

	_form.KillTimer(nTimer);
	//-- used to handle form tab refresh on bpm state change
	if(nTimer==5005)_swdoc.setup_process_fields(true);	
	if(nTimer==1001)
	{
		var progress = _swdoc.OC().bpm_progress;
		var i = 0;
		var check = true;
		if(progress < 1)
		{
			check = false;
		}
		while(_swdoc.MF().si_progress1.GetItemText(i, 1))
		{
			var ret = _swdoc.MF().si_progress1.SetRowChecked(i, check)
			if(_swdoc.MF().si_progress1.GetItemText(i, 1) == progress)
			{
				check = false;
			}
			i++;
		}
	}	


}

						TabDet_OnTabSelected(nTab)
{
	var tAuth = 1;
	var tProgress=3;
	var tTasks = 2;
	var tChange =5;
	if(nTab == tProgress)
	{
		mainform.SetTimer(1001, 10);
	}
	else if(nTab == tTasks)
	{
		_swdoc.boolRefreshTasks = true;
		_swdoc.MF().sl_tasks.Refresh();
	}
	else if(nTab==tAuth)
	{
		_swdoc.MF().sl_wfauth.Refresh();
	}
	else if(nTab==tChange)
	{
		app._slf(_swdoc.MF().sl_activity1 , "cr="+_swdoc.opencall.callref);
		_swdoc.MF().sl_activity1.Refresh();
	}

}

