						sl_activity1_OnItemDoubleClicked(nRow, nCol)
{
	// TODO: Add your event handler code here
	var strKeyValue = sl_activity1.GetItemTextRaw(nRow, 'pk_auto_id');
	//-- F0091939
	if(strKeyValue=="")
		return;

	var strCIids = app.g.sqllist_get_valuesdel(_swdoc.EF().sl_configitems1, 'pk_auto_id', ",", "");
	var strServiceids = app.g.sqllist_get_valuesdel(_swdoc.EF().sl_services, 'pk_auto_id', ",", "");
	if(strCIids!="" && strServiceids!="")
	{
		strCIids+=",";
		strCIids+=strServiceids;
	}
	var strURL = "fk_callref=" + _swdoc.OC().callref +"&ci_ids="+strCIids+"&requireBy="+_swdoc.OC().itsm_requiredbyx+"&release=1";
	app.OpenFormForEdit("change_activity", strKeyValue, strURL, true);
	sl_activity1.Refresh();
	_swdoc.check_act_warnings();

}

