						TabRisk_OnTabSelected(nTab)
{
	// TODO: Add your event handler code here
	if(nTab==1)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (_swdoc.EF().sl_configitems1.rowCount() < 1) app._slf(_swdoc.EF().sl_configitems1, "cr="+_swdoc.OC().callref+"&code=RELEASE&type=1");
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (_swdoc.EF().sl_kes1.rowCount() < 1) app._slf(_swdoc.EF().sl_kes1.filter, "cr="+_swdoc.OC().callref+"code=RELEASE-ERROR");
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (_swdoc.EF().sl_rels1.rowCount() < 1) app._slf(_swdoc.EF().sl_rels1, "cr="+_swdoc.OC().callref+"&code=RELEASE-RFC");
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (_swdoc.EF().sl_incs1.rowCount() < 1) app._slf(_swdoc.EF().sl_incs1, "cr="+_swdoc.OC().callref+"&code=RELEASE-INCIDENT");

		_swdoc.filter_impact_customers(_swdoc.EF());
		//oForm.sl_customerimpact1.filter	
		app._ete(_swdoc.MF().tb_no_of_custs,_swdoc.EF().sl_customerimpact.rowCount());

		if(_swdoc.MF().sl_childitems.rowCount()<1)
		{
			var strChildKeys = "";
			var strCIKeys = app.g.sqllist_get_valuesdel(_swdoc.EF().sl_configitems1, 'pk_auto_id', ",", "");
			var strServiceKeys = app.g.sqllist_get_valuesdel(_swdoc.EF().sl_services, 'pk_auto_id', ",", "");
			if(strServiceKeys!="")
			{
				if(strCIKeys!="")strCIKeys+=",";
				strCIKeys+=strServiceKeys;
			}

			if(strCIKeys!="")
				app._slf(_swdoc.MF().sl_childitems,"cis="+strCIKeys);
		}
		_swdoc.MF().sl_childitems.Refresh();

	}
	else if(nTab==2)
	{
		app._slf(_swdoc.MF().sl_risks1 , "cr="+_swdoc.opencall.callref+"&code=RELEASE-RISK");
		_swdoc.MF().sl_risks1.Refresh();
	}

}

