						btn_bpm_progress_OnPressed()
{
	if(IsObjectDefined("_bHtmlWebClient"))
    {
    	var getColourScheme = 4; //silver default
    }else
    {
    	var getColourScheme = app.g.get_CurrentSkinColorSchema();
    }

	var result = app.OpenHtmlWindow("BPM_PROGRESS_1", "frame", "&[app.webroot]/clisupp/details/&[app.currentdd]/itsm_bpmcall.php?callref="+_swdoc.OC().callref+"&sessid="+app.session.sessionId+"&ColourScheme="+getColourScheme, 
                                  "BPM Progress", true);
}

						btn_actionsts_OnPressed()
{
	// TODO: Add your event handler code here
	var xmlmc = new XmlMethodCall;

	// Set values for the Input parameters - Parameter values must be set in the same order they are expected by the method.
	xmlmc.SetParam("callref", _swdoc.OC().callref); 				

	// Invoke the VPME method LogCall.
	if(xmlmc.Invoke("VPME", "clearActionStatus"))
	{
    	// Success, lets access output parameters
    	app._een(_swdoc.MF().btn_actionsts, false);
	}
	else
	{
    	// Error
    	MessageBox(xmlmc.GetLastError());
	}


}

						TabBackout_OnTabSelected(nTab)
{
	// TODO: Add your event handler code here
	if(nTab==1)
	{
		app._slf(_swdoc.MF().sl_bplans1 , "cr="+_swdoc.opencall.callref+"&code=RELEASE-BPLAN");
