}

						btn_risk_review_OnPressed()
{
	// TODO: Add your event handler code here
	var strURL = "ila_options="+_swdoc.strRiskOptions+"&appcode="+app.itsm.get_session_param("dataset")+"&usage_area=Release Risk&mode=details form&callref="+_swdoc.OC().callref;
	app.OpenForm("EfImpactAssessmentEntry", strURL, true, function(oRes)
	{
		if (oRes.document.boolApplied)
		{
		// store previous values for history and possible diary entries
		var strOrigRiskOptions = _swdoc.strRiskOptions;
		_swdoc.strOrigRisk = _swdoc.OC().itsm_risk_level;
		_swdoc.strOrigRiskWeighting = _swdoc.OC().itsm_risk_weighting; 

		_swdoc.strRiskOptions = oRes.document.strSelectedKeys;
		app.g.relate_ila_options_to_call(_swdoc.OC().callref,"Release Risk",oRes.document.strSelectedKeys);
		_swdoc.OC().itsm_risk_weighting = oRes.document.intWeighting;
		_swdoc.OC().itsm_risk_level = oRes.document.strImpactLevel;
		_swdoc.UpdateFormFromData();


		}
	});

}

						mb_rel_actions_OnMenuItem(strName, nItemID)
{

	var iChange = 1;
	var iAddKB = 2;
	if(nItemID==iChange)
	{

		//-- RJC 2.4.0 FR
		//-- store info in global vars
		app.g.LOG_FROM_CALL = _swdoc.opencall;
		app.g.CALLINFO[_swdoc.OC().callref] = new Object();
		app.g.CALLINFO[_swdoc.OC().callref].origupdatetxt = itsm_opencall_rfc.dow_note;

		//-- open new problem record
		app.global.LogNewCall("Change Request");
    }else if (nItemID==iAddKB)
	{
		//ES - F0112200 - Create KB Document from Change Request Details
		app.OnNewKBDocument(app.session.analystId,_swdoc.OC().probcode,app.g.callref_pad(_swdoc.OC().callref),itsm_opencall_rfc.dow_note,"","",_swdoc.OC().itsm_title,"",app.g.callref_pad(_swdoc.OC().callref));
	}

}

						jscript_OnMouseOver(bMouseOver)
{
    //-- CALLED WHEN CALL IS CANCELLED
    //-- nwj - 25.03.2004
    //-- remove all incidents and problems from the rfc
    var callref_id = _swdoc.opencall.callref;

    //-- doing a cancel
    if (bMouseOver == 0)
    {

    //-- DELETE ASSOCIATED INCIDENT RELATIONSHIPS   
    for (var y=0; y < sl_aff_incidents.rowCount(); y++)
    {
        var incident_callref = sl_aff_incidents.GetItemTextRaw(y,0);
        var incident_callref_text = sl_aff_incidents.GetItemText(y,0);


        if (incident_callref != "")
        {
            //-- ok to delete
            //-- perform delete here to rel_inc2ci
            var taskObject = new Object();
            taskObject.task = "del_rfc2inc";
            taskObject.rfcref = callref_id;
            taskObject.incref = incident_callref;
            taskObject.inctext = incident_callref_text;

            if (jscript_OnLeftClick(taskObject))
            {

                //-- update rfc diary
                var strText = "RFCM - Incident [" + incident_callref_text + "] was removed from the RFC Incident list";
                var taskObject = new Object();
                taskObject.task = "updatecalldiary";
                taskObject.callref = callref_id;
                app._ete(taskObject , strText);
                jscript_OnLeftClick(taskObject);
    
                //-- update removed incident diary
                var strText = "RFCM - Incident was dis-associated from RFC [" + lbl_cref_string.text + "] as the RFC was cancelled or closed without resolution";
                var taskObject = new Object(); 
                taskObject.task = "updatecalldiary";
                taskObject.callref = incident_callref;
                app._ete(taskObject , strText);
                jscript_OnLeftClick(taskObject)

            }
        }
    }

    //-- DELETE ASSOCIATED PROBLEM RELATIONSHIPS   
    for (var y=0; y < sl_aff_problems.rowCount(); y++)
    {
        var prb_callref = sl_aff_problems.GetItemTextRaw(y,0);
        var prb_callref_text = sl_aff_problems.GetItemText(y,0);

        if (prb_callref != "")
        {
            //-- ok to delete
            //-- perform delete here to rel_inc2ci
            var taskObject = new Object();
            taskObject.task = "del_rfc2prob";
            taskObject.rfcref = callref_id;
            taskObject.prbref = prb_callref;
            taskObject.prbtext = prb_callref_text;

            if (jscript_OnLeftClick(taskObject))
            {

                //-- update RFC diary
                var strText = "RFCM - Problem [" + prb_callref_text + "] was removed from the RFC Problem list";
                var taskObject = new Object();
                taskObject.task = "updatecalldiary";
                taskObject.callref = callref_id;
                app._ete(taskObject , strText);
                jscript_OnLeftClick(taskObject);
    
                //-- update removed problem diary
                var strText = "RFCM - Problem was dis-associated from RFC [" + lbl_cref_string.text + "] as the RFC was cancelled or closed without resolution";
                var taskObject = new Object(); 
                taskObject.task = "updatecalldiary";
                taskObject.callref = prb_callref;
                app._ete(taskObject , strText);
                jscript_OnLeftClick(taskObject)

            }
        }
    }

    //-- DELETE ASSOCIATED CI RELATIONSHIPS
     for (var y=0; y < mainform.sl_configitems.rowCount(); y++)
    {
        //-- do this by finding unselected rows and making sure they are part of fitler
        var pk_ci_id = mainform.sl_configitems.GetItemTextRaw(y,0);
        var ck_item_key = mainform.sl_configitems.GetItemTextRaw(y,2)

   
        if (pk_ci_id != "")
        {
    
            //-- delete relationship
            var taskObject = new Object();
            taskObject.task = "del_rfc2ci";
            taskObject.rfcref = callref_id;
            taskObject.ciref = pk_ci_id;
            taskObject.causedby = 0;
            
            if (jscript_OnLeftClick(taskObject))
            {
                //-- update problem diary
                var strText = "RFCM - Configuration Item [" + ck_item_key + "] was removed from the RFC";
                var taskObject = new Object();
                taskObject.task = "updatecalldiary";
                taskObject.callref = callref_id;
                app._ete(taskObject , strText);
                jscript_OnLeftClick(taskObject);
            }     
        }
    }

    }
    //--eof cancel

    //-- doing a close without resolve
    if (bMouseOver == 1)
    {
        //-- UPDATE ASSOCIATED INCIDENT RELATIONSHIPS  DIARY
        for (var y=0; y < sl_aff_incidents.rowCount(); y++)
        {
            var incident_callref = sl_aff_incidents.GetItemTextRaw(y,0);
            var incident_callref_text = sl_aff_incidents.GetItemText(y,0);

            if (incident_callref != "")
            {
                //-- update incident diary
                var strText = "RFCM - RFC [" + lbl_cref_string.text + "] that this incident is related to has been closed without resolution.";
                var taskObject = new Object(); 
                taskObject.task = "updatecalldiary";
                taskObject.callref = incident_callref;
                app._ete(taskObject , strText);
                jscript_OnLeftClick(taskObject)

            }
        }


        //-- ASSOCIATED PROBLEM RELATIONSHIPS   DIARY
        for (var y=0; y < sl_aff_problems.rowCount(); y++)
        {
            var prb_callref = sl_aff_problems.GetItemTextRaw(y,0);
            var prb_callref_text = sl_aff_problems.GetItemText(y,0);

            if (prb_callref != "")
            {
                //-- update removed problem diary
                var strText = "RFCM - RFC [" + lbl_cref_string.text + "] that this problem is related to has been closed without resolution.";
                var taskObject = new Object(); 
                taskObject.task = "updatecalldiary";
                taskObject.callref = prb_callref;
                app._ete(taskObject , strText);
                jscript_OnLeftClick(taskObject)

            }
        }
        
    }

}

						jscript_OnRightClick()
{
    //-- NWJ - 25.03.2004
    //-- Resolve associated problems and incidents for this RFC
    var callref_id = _swdoc.opencall.callref;

    if (_swdoc.itsm_settings.rfc_ari == 1)
    {

        //-- UPDATE ASSOCIATED INCIDENT RELATIONSHIPS TO RESOLVED
        for (var y=0; y < sl_aff_incidents.rowCount(); y++)
        {
            var incident_callref = sl_aff_incidents.GetItemTextRaw(y,0);
            incident_callref++;incident_callref--;
            var incident_callref_text = sl_aff_incidents.GetItemText(y,0);

            if (incident_callref != "")
            {
                var strText = "Incident Resolved due to RFC resolution [" + lbl_cref_string.text + "]";

                //-- set incident to resolved
                var hdcon = new HelpdeskSession;
                if(hdcon.Connect())
                {
                    if(hdcon.BeginCloseCall(incident_callref))
                    {   
                        //-- We send our transaction values one by one
                        hdcon.SendText ("description", strText);
                        hdcon.SendNumber("status",6); // if you want resolved change this to 6
                        hdcon.SendNumber("backdateperiod",0);
                        hdcon.SendNumber("timespent",1);
                        hdcon.Commit();
                    }
                    //-- We have finished with the connection, close it.    
                    hdcon.Close();
                }
            }
        }
    }


    if (_swdoc.itsm_settings.rfc_arp == 1)
    {

        //-- UPDATE related problems to resolved
        for (var y=0; y < sl_aff_problems.rowCount(); y++)
        {
            var prb_callref = sl_aff_problems.GetItemTextRaw(y,0);
            prb_callref++;prb_callref--;
            var prb_callref_text = sl_aff_problems.GetItemText(y,0);

            if (prb_callref != "")
            {
                var strText = "Problem Resolved due to RFC resolution [" + lbl_cref_string.text + "]";

                //-- set incident to resolved
                var hdcon = new HelpdeskSession;
                if(hdcon.Connect())
                {
                    if(hdcon.BeginCloseCall(prb_callref))
                    {   
                        //-- We send our transaction values one by one
                        hdcon.SendText ("description", strText);
                        hdcon.SendNumber("status",6); 
                        hdcon.SendNumber("backdateperiod",0);
                        hdcon.SendNumber("timespent",1);
                        hdcon.Commit();
                    }
                    //-- We have finished with the connection, close it.    
                    hdcon.Close();
                }
            }
        }
    }
    return true;
}

						TabRel_OnTabSelected(nTab)
{
//-- NWJ - 28.11.2006
	//-- Handle tab clicking
	tICIs = 0;
	tRFCs = 2;
	tSRs = 3;
	tKEs = 4;	
	tIncidents = 5;
	tKB = 7;
	tCustomers = 8;
	tIService = 1;
	tEffect = 6;
	tTasks = 9;	
	tRAKb = 10;

	if(nTab==tICIs)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (sl_configitems1.rowCount() < 1) sl_configitems1.Refresh();
	}
	else if(nTab==tIService)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (sl_services.rowCount() < 1) sl_services.Refresh();
	}
	else if(nTab==tKEs)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (sl_kes1.rowCount() < 1) sl_kes1.Refresh();
	}
	else if(nTab==tSRs)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (sl_srs.rowCount() < 1) sl_srs.Refresh();
	}	
	else if(nTab==tRFCs)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (sl_rels1.rowCount() < 1) sl_rels1.Refresh();
	}	
	else if(nTab==tIncidents)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (sl_incs1.rowCount() < 1) sl_incs1.Refresh();
	}
	else if(nTab==tCustomers)
	{
		//-- load affected customers
		_swdoc.filter_impact_customers(_swdoc.EF());
		_swdoc.EF().sl_customerimpact.Refresh();
		//-- load affected customers
		_swdoc.filter_impact_orgs(_swdoc.EF());
	}
	else if(nTab==tKB)
	{
		if (sl_kbs1.rowCount() < 1)
		{
			sl_kbs1.Refresh();
		}

	}
	else if(nTab==tEffect)
	{
		if(TabCause.tab==0)
		{
			//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
			if (sl_causeinc1.rowCount() < 1) sl_causeinc1.Refresh();
		}
		else
		{
			//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
			if (sl_causeprob1.rowCount() < 1) sl_causeprob1.Refresh();
		}
	}
	else if(nTab==tTasks)
	{
		if (_swdoc.EF().sl_taskhistory.rowCount() < 1)
		{
			app._slf(_swdoc.EF().sl_taskhistory,"swc=bpm_parentcallref&kvs="+_swdoc.OC().callref);

			_swdoc.EF().sl_taskhistory.Refresh();
			_swdoc.EF().sl_taskhistory.SetRowSelected(0);
		}
	}
	else if(nTab==tRAKb)
	{
		if (sl_rakb.rowCount() < 1)
		{
			sl_rakb.Refresh();			
		}
	}	

}

						mbtn_cci_views1_OnMenuItem(strName, nItemID)
{

	var iProperties = 1;
	var iVCMParents = 2;	
	var iVCMChildren = 5;	
	var iInventory = 3;
	var iChangeSchedule = 4;

	//-- get intCI
  	var intCI = app.g.sl_getrawcolvalue(sl_configitems1,-1,"pk_auto_id");
  	if(intCI>0)
  	{
  		//-- pass in text identfier as well
  		var strItem = app.g.sl_getrawcolvalue(sl_configitems1,-1,"ck_config_item");

		if(nItemID == iProperties)
		{
			app.cmdb.popup_configitem(intCI,true,"",false,function()
			{
				return;
			});
    	}
		else if(nItemID == iVCMParents)
		{
			app.cmdb.view_vcm(intCI,true);
		}
		else if(nItemID == iVCMChildren)
		{
			app.cmdb.view_vcm(intCI,false);
		}
		else if(nItemID == iInventory)
		{
			app.cmdb.view_inventory(intCI,strItem);
		}
		else if(nItemID == iChangeSchedule)
		{
			app.cmdb.view_schedule(intCI,strItem);
		}
	}
}

						mbtn_cci_actions1_OnMenuItem(strName, nItemID)
{

	var iRemove = 1;	
	if(nItemID==iRemove)
	{
		//-- remove CI
		if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;

      	//-- NWJ - 15.03.2004
      	//-- Remove Selected config items
		var strCIs = app.g.sl_getselected_valuesdel(sl_configitems1,"pk_auto_id",",");
		if(strCIs!= "")
		{
			var strCItexts = app.g.sl_getselected_valuesdel(sl_configitems1,"ck_config_item",",");
			_swdoc.remove_release_cis(strCIs , strCItexts);
      	}
	}
}

