						mbtn_ke_actions1_OnMenuItem(strName, nItemID)
{
	var iDisassociate = 1;

	//-- NWJ - unlink incident
	if(nItemID == iDisassociate)
	{
		if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;

		var strCallRefs = app.g.sl_getselected_valuesdel(sl_kes1,"callref",",");
		if(strCallRefs!= "")
		{
			if(app.g.unrelate_slave_calls(_swdoc.OC().callref,strCallRefs,'RELEASE-ERROR'))
			{
				var arrCallrefs = strCallRefs.split(',');
				var strText = "This release request has been dis-associated from the following known errors\n\n";

				for(var x=0;x<arrCallrefs.length;x++)
				{
					strText += "Known Error (" + app.g.callref_pad(arrCallrefs[x]) + ") dis-associated\n";

					var strKEText = "The release request (" + app.g.callref_pad(_swdoc.OC().callref) + ") has been dis-associated from this Known Error";
					app.g.new_diaryevent(arrCallrefs[x], strKEText,"Release Management","Release Dis-association");

				}


				app.g.new_diaryevent(_swdoc.OC().callref,strText,"Release Management","Known Error Dis-association");
			}

			sl_kes1.Refresh();

		}	
	}

}

