	app._een(mbtn_inc_actions3,boolActive);



}

						mbtn_inc_views3_OnMenuItem(strName, nItemID)
{
	var strKeyValue = app.g.sl_getcolvalue(sl_kbs1, sl_kbs1.curSel, "docref");
	app.oKB.open_article_form(strKeyValue);
}

						mbtn_inc_actions3_OnMenuItem(strName, nItemID)
{
	var iDisassociate = 1;

	if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;

	//-- NWJ - unlink release
	if(nItemID == iDisassociate)
	{

		var strText = "This Release Request has been dis-associated with the following KB Documents\n\n";
		var strDocs = app.g.sl_getselected_valuesdel(sl_kbs1,0,",");
		if(strDocs!= "")
		{
			app.oKB.unrelate_calls_from_kb(strDocs,_swdoc.OC().callref);
			sl_kbs1.Refresh();
			strText += "Knowledgebase Document (" + strDocs + ") dis-associated\n";
			app.g.new_diaryevent(_swdoc.OC().callref,strText,"Release Management","Knowledgebase Document Dis-association");
		}	
	}	
}

						btn_search_inc3_OnPressed()
{
    app.g.search_for("swkb", true,"",function(oRes)
    {
    	var strKeys = oRes.selectedkeys;
		if(strKeys=="")return;
		var arrKeys = strKeys.split(",");
 		var strText = "This Release Request has been associated with the following Knowledgebase Documents\n\n";
		for (var x=0; x< arrKeys.length;x++)
		{
			var fk_key = arrKeys[x];
			if(app.g.sl_findrow_byvalue(sl_kbs1,fk_key,"docref")==-1)
			{
				app.oKB.relate_call_to_kb(fk_key,_swdoc.OC().callref,'Modify');
				strText += "Knowledgebase Document (" + fk_key + ") associated\n";
			}
		}			
		app.g.new_diaryevent(_swdoc.OC().callref ,strText,"Release Management","Knowledgebase Document Association");

		sl_kbs1.Refresh();
		sl_kbs1.SetRowSelected(0);
		sl_kbs1_OnItemSelected(0);
		return;
    });
}

						mbtn_inc_views5_OnMenuItem(strName, nItemID)
{
	var strKeyValue = app.g.sl_getcolvalue(sl_rakb, sl_rakb.curSel, "docref");
	app.g.open_article(strKeyValue);
}

						mbtn_inc_actions4_OnMenuItem(strName, nItemID)
{
	var iDisassociate = 1;

	if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;

	//-- DJH - unlink KB doc
	if(nItemID == iDisassociate)
	{

		var strText = "This release request has been dis-associated with the following KB Documents\n\n";
		var strDocs = app.g.sl_getselected_valuesdel(sl_rakb,0,",");
		if(strDocs!= "")
		{
			//app.oKB.unrelate_calls_from_kb(strDocs,_swdoc.OC().callref);
			app.g.unrelate_call_from_rakb(strDocs,_swdoc.OC().callref)
			sl_rakb.Refresh();

			strText += "Knowledgebase Document (" + strDocs + ") dis-associated\n";
			app.g.new_diaryevent(_swdoc.OC().callref,strText,"Release Management","Knowledgebase Document Dis-association");
		}	
	}	
}

						btn_search_inc4_OnPressed()
{
     if(!app.g.use_sw_knowledgebase())
			{
				app.allocatekbdoc(_swdoc._kbidentifier,_swdoc);
				app.g.open_third_party_kb_search(true, "", _swdoc.OC().callref,app.g.sl_get_valuesdel(_swdoc.EF().sl_rakb,0,","), _swdoc._kbidentifier);
	}

	return;
}

