function load_details()
{
	var strParams = "fksla="+_swdoc.oRecIncident.itsm_sladef;
	if(_swdoc.strCIs!="" && _swdoc.strCIs!=undefined)
		strParams += "&cikeys="+_swdoc.strCIs;

	app._slf(_swdoc.MF().lb_oc_olad, strParams);
	_swdoc.OC().priority = "";
	_swdoc.OC().itsm_sladef = "";
	app._ete(_swdoc.MF().flg_pauseparent, "0");
}

function handle_set_assignment(strSupportGroup)
{

}

function handle_set_oladef(strNewOLD)
{
	var oRecOLD = app.g.get_record("itsmsp_slad_ola", strNewOLD);
	if(oRecOLD)
	{	
		_swdoc.OC().priority = oRecOLD.fk_sla;	
		app._een(_swdoc.MF().lb_oc_sla, true);	
		app._een(_swdoc.MF().oc_impact,true);
		app._een(_swdoc.MF().oc_urgency, true);
		app._ete(_swdoc.MF().fld_desc, oRecOLD.descriptive);

		if(dd.GetGlobalParamAsNumber("Application Settings/OLA/UseMatrixPriority")==1)
		{
			var strSLA = app.itsm.get_sla_from_slad_matrix(strNewOLD, _swdoc.OC().itsm_impact_level, _swdoc.OC().itsm_urgency_level, 0);
			if(strSLA!="")
				_swdoc.OC().priority = strSLA;	
		}


		if(_swdoc.OC().suppgroup != oRecOLD.fk_suppgroup)
		{
			if(_swdoc.OC().suppgroup!="" && oRecOLD.fk_suppgroup != '[All Support Groups]')
			{
				MessageBox("The assignment group has been changed to the group defined against the OLD.");
			}

			if(oRecOLD.fk_suppgroup == '[All Support Groups]')
			{
				MessageBox("Please select the support group to assign this task to.");
				_swdoc.OC().suppgroup = "";
				_swdoc.OC().owner = "";
				app._een(_swdoc.MF().tb_suppgroup, true);
				app._een(_swdoc.MF().tb_suppowner, true);
				app._ete(_swdoc.MF().tb_suppowner, "");
			}else
			{
				_swdoc.OC().suppgroup = oRecOLD.fk_suppgroup;
				_swdoc.OC().owner = oRecOLD.fk_aid;
				app._ete(_swdoc.MF().tb_suppowner, "");
				app._een(_swdoc.MF().tb_suppgroup, false);
				app._een(_swdoc.MF().tb_suppowner, false);
			}
			_swdoc.UpdateFormFromData();
		}

		_swdoc.calc_priority();

	}
}

function calc_priority()
{
	var utccalllogdate = new Date();
	if(_swdoc.OC().priority=="False")
		return;
	var useSLA = app.g.get_sla_timeinfo(_swdoc.OC().priority,utccalllogdate);

	var mcFixDate = new Date();
	mcFixDate.setTime(_swdoc.oRecIncident.fixbyx * 1000);

	app._ete(_swdoc.MF().tb_ola_respby,useSLA.sla_tz_respbyx);
	app._ete(_swdoc.MF().tb_ola_fixby, useSLA.sla_tz_fixbyx);		

	//-- if ola fix by is greater than parent sla fix by alert the analyst
	_swdoc.boolOLAImpactsParent = (useSLA.sla_tz_fixbyx > app.g.fixepoch(new Number(_swdoc.oRecIncident.fixbyx)));
	app._eva(_swdoc.MF().lb_impact,(_swdoc.boolOLAImpactsParent)?"2":"1");

	if (_swdoc.boolOLAImpactsParent)
	{
		MessageBox("Warning : The estimated OLA resolution date exceeds the resolution date of the parent SLA.");
	}
}

						OnFormLoading(strFormName, strCallclass)
{
	//-- can they raise an this type of call
	if(app.itsm.can_create(strCallclass,true)==false) return false;


	if(app.g.LOG_FROM_CALL==null)
	{
		MessageBox("A " + strCallclass + " can only be raised from an incident detail form.");
		return false;
	}
	else
	{
		//-- store call object so we can load related info

		_swdoc.oRecIncident = app.g.LOG_FROM_CALL;//app.g.get_sys_record("opencall", app.g.LOG_FROM_CALL.callref);
		opencall.bpm_parentcallref = _swdoc.oRecIncident.callref;
		app.g.LOG_FROM_CALL=null;
	}

	return true;
}

						OnRecordValueChanged(strTable, strColumn, strValue, strOldValue)
{
	// TODO: Add your event handler code here

	if( (strColumn=="itsm_impact_level")||(strColumn=="itsm_urgency_level") )
    {
    	//-- if use incident assessment over ci
    	if(dd.GetGlobalParamAsNumber("Application Settings/OLA/UseMatrixPriority")==0)
    	{
    		if(_swdoc.OC().itsm_sladef!="")
    		{ 			
    			var strSLA = app.itsm.get_sla_from_slad_matrix(_swdoc.OC().itsm_sladef, _swdoc.OC().itsm_impact_level, _swdoc.OC().itsm_urgency_level,0);
    			if(strSLA!="")
    			{
    				if(strSLA!=_swdoc.OC().priority)
    				{
    					_swdoc.OC().priority = strSLA;
    					_swdoc.calc_priority();
  			  			_swdoc.UpdateFormFromData();
    				}
    			}
    		}
    	}
    }
	else if(strColumn=="probcode" && strTable == "opencall")
    {
		_swdoc.boolChangeProfile = true;
  		mainform.SetTimer(4, 100);
	}
	else if(strColumn=="priority" && strTable == "opencall")
    {
    	if(_swdoc.boolChangeProfile)
    	{
			_swdoc.OC().priority = strOldValue;
			_swdoc.UpdateFormFromData();
			MessageBox("SLAs and Priorities are dependant on OLA Task, so cannot be set from the request profile.");
   		}
    }


}

