						tb_suppowner_OnRightClick()
{
	_swdoc.OC().suppgroup = "";
	_swdoc.OC().owner = "";		
	app._ete(tb_suppowner, "");
	//-- filter olas that are defined for the master incident SLA
	_swdoc.handle_set_assignment("");

	_swdoc.UpdateFormFromData();
	return false;
}

						tb_suppowner_OnDropDown()
{
	//-- pick analyst or group
	var oTmp = app.g.pick_analystorgroup("Assign To");
	if((oTmp)&&(oTmp.oGroup!=false))
	{
		//-- we have info
		_swdoc.OC().suppgroup = oTmp.oGroup.id;
		_swdoc.OC().owner = oTmp.oAnalyst.id;		

		_swdoc.handle_set_assignment(oTmp.oGroup.id)
		_swdoc.UpdateFormFromData();
	}

	return false;
}

						OnFormLoaded(bDataReloaded)
{
	//-- load incident details into form fields..note have to do js for this as we cant bind two records from same table onto a form
	app.g.load_form_fields(_swdoc.oRecIncident, mainform, "incident");
	_swdoc.OC().itsm_impact_level = _swdoc.oRecIncident.itsm_impact_level;
	_swdoc.OC().itsm_urgency_level = _swdoc.oRecIncident.itsm_urgency_level;
	_swdoc.opencall.suppgroup = "";
	opencall.owner = "";

	_swdoc.strCIs = app.cmdb.get_call_cis(_swdoc.oRecIncident.callref);

	//-- filter olas that are defined for the master incident SLA
	_swdoc.load_details();

}

						tb_suppgroup_OnLeftClick()
