						var _swdoc = (IsObjectDefined("_bHtmlWebClient"))?top:_swdoc;

var boolOnHoldNotification = false;
//Get Setting
_swdoc.boolOnHoldNotification = app.oSettings.get_setting("boolOnHoldNotification");

