{
	return _swdoc.mainform;
}

							OnFormLoaded(bDataReloaded)
{
	//###20120705
	mainform.tSLM.ShowTabItem(5, _swdoc.boolOnHoldNotification);
	app._etab(tSLM , 0);
	if(!app.session.HaveAppRight(app.ADMIN_SETTINGS, app.SLM_VIEW, app.session.dataDictionary)) 
	{
		MessageBox("You do not have permission to view SLM.");
		_swdoc.CloseForm();
		return false;
	}
	//-- Analyst Rights
	if(!app.session.HaveRight(ANALYST_RIGHT_C_GROUP,ANALYST_RIGHT_C_CANMANAGESLAS))
	{
		MessageBox("You do not have permission to manage SLM.");
		_swdoc.CloseForm();
		return false;
	}
	else
	{
		//-- TK Load Tab
		mainform.tSLM_OnTabSelected(0);
	}	


}

