							btn_view_slad3_OnPressed()
{
//	sl_priorities.EditRecord(sl_slad2.curSel);
	var strSLA =  app.g.sqllist_getselected_value(sl_priorities1, "priority");
	if(strSLA!="")
	{
		var oSLA = false;
		var oRS = app.g.get_sqrecordset("call/common/get_priority_details", "nm="+app.pfu(strSLA));
		if(oRS.Fetch())
		{
			var intCols = oRS.GetColumnCount();

			oSLA = new Object();
			for (x=0; x < intCols;x++)
			{
				var colName = oRS.GetColumnName(x);
				var colValue = app.g.get_field(oRS,x);
				oSLA[app.LC(colName)] = colValue;
			}
		}
		app.global.InvokeSlaEditDialog(oSLA.slaid, false, "Priority");
	}
	//MessageBox(strSLA);
	//sl_priorities.SetRowSelected(sl_slad2.curSel);
	//sl_slad1_OnItemSelected(sl_slad2.curSel);		

}

							btn_remove_slad3_OnPressed()
{
	if(!app.session.HaveAppRight(app.ADMIN_SETTINGS, app.SLM_DELETE, app.session.dataDictionary)) 
	{
		MessageBox("You do not have permission to add delete in SLM.");
		return false;
	}

	var varKey = sl_priorities1.GetItemTextRaw(sl_priorities1.curSel,'priority');
	if(varKey!="")
	{
		var intRowCount = app.g.sqs_rowcount("count/slas_related_to_priorities","kv="+app.pfu(varKey));
		if(intRowCount>0)
		{
			MessageBox('This priority is associated to SLAs/OLAs. Please dis-associate before deleting Priority.');
			return false;
		}

		var strConfirm = "Are you sure you want to delete the selected priority record?";
		if(confirm(strConfirm))
		{
			var strParams = "nm="+app.pfu(varKey);
			var oSLA = app.g.get_sqrecordset("call/common/get_priority_details",strParams); 
			if(oSLA.Fetch())
			{
				var colValue = app.g.get_field(oSLA,"slaid");

				var bDeleted = app.global.DeleteSla(colValue);
				if(!bDeleted)
				{
					MessageBox('Unable to delete the selected Priority');
					return false;
				}
				sl_priorities1.Refresh();
			}
		}
	}

}

