							tSLM_OnTabSelected(nTab)
{
	// TODO: Add your event handler code here	
	var tSLR = 1;
	var tSLA = 0;
	var tOLA = 2;
	var tPriority = 3;
	var tEscalation = 4;
	var tOnHold = 5;
	if(nTab==tSLA)
	{
		var intShowArchivedSLAs = _swdoc.MF().flg_show_archived_slas.value;
		var strFilter = "ac=SLA&af=" + intShowArchivedSLAs;
		app._slf(_swdoc.MF().sl_slad1,strFilter);
		_swdoc.MF().sl_slad1.Refresh();
	}
	else if(nTab==tOLA)
	{
		_swdoc.MF().sl_ola.Refresh();
	}
	else if(nTab==tPriority)
	{
		_swdoc.MF().sl_priorities.Refresh();
		var strPriorities = app.g.sqllist_get_valuesdel(sl_priorities, "fk_priority", ",", "");
		if(strPriorities=="")strPriorities="";
		var strFilter = "kv="+strPriorities;
		app._slf(sl_priorities1,strFilter);
		_swdoc.MF().sl_priorities.Refresh();
		_swdoc.MF().sl_priorities1.Refresh();
		app._een(btn_add_slad6,(sl_priorities.curSel>-1));
		app._een(btn_view_slad4,(sl_priorities.curSel>-1));
		app._een(btn_remove_slad3,(sl_priorities1.curSel>-1));
		app._een(btn_view_slad3,(sl_priorities1.curSel>-1));
		app._een(btn_add_slad5,(sl_priorities1.curSel>-1));


	}
	else if(nTab==tEscalation)
	{
		_swdoc.MF().sl_ola1.Refresh();

	}
	else if(nTab==tOnHold)
	{
		_swdoc.MF().sl_ohn.Refresh();
	}
	else if(nTab==tSLR)
	{
		_swdoc.MF().sl_slr.Refresh();
	}
}

