							btnOHNDelete_OnPressed()
{
    //-- nwj :- use key col name instead of numbering
    var sCol = dd.tables[sl_ohn.table].PrimaryKey;
    if(sCol=="")
    {
            MessageBox("No primary key is defined for this table. Please contact your Supportworks Administrator");
    }
    else
    {
            if(sl_ohn.curSel != -1)
            {

                    //--  ask them to confirm
                    if(!confirm('Are you sure you want to delete the selected record?'))return false;

                    var strKeyValue = sl_ohn.GetItemTextRaw(sl_ohn.curSel, sCol);
                    var strParams = "kv="+app.pfu(strKeyValue);
                    if(app.g.submitsqp("form/manage_itsm_processes/delete_onhold_notifier",strParams))
                    {
                    	sl_ohn.RemoveRow(sl_ohn.curSel);
                    	sl_ohn.Refresh();
                    }
            }
    }
}

