							mbtn_actions_OnMenuItem(strName, nItemID)
{
	var iSLA = 1;
	if(nItemID==iSLA)
	{
		var intIndex = app.g.sl_getmaxvalue(sl_slad1,'dindex');
		var strName = _swdoc.MF().sl_slr.GetItemText(_swdoc.MF().sl_slr.curSel ,0); //itsmsp_slr.title
		var strOwner = _swdoc.MF().sl_slr.GetItemText(_swdoc.MF().sl_slr.curSel ,2); //itsmsp_slr.requested_by
		var intID = _swdoc.MF().sl_slr.GetItemText(_swdoc.MF().sl_slr.curSel ,3); //itsmsp_slr.pk_slr_id
		var strDesc = _swdoc.MF().sl_slr.GetItemText(_swdoc.MF().sl_slr.curSel ,4); //itsmsp_slr.functionality


		var strURL = "dindex=" + ++intIndex + "&fk_keysearch=" + strOwner + "&slad_id="
					+ strName + "&descriptive=" + strDesc + "&fk_aid=" + app.session.analystId
					+ "&slr_id=" + intID;

		app.OpenFormForAdd("itsmsp_slad", "", strURL, true, function(){
			sl_slad1.Refresh();
			sl_slad1.SetRowSelected(0);
			sl_slad1_OnItemSelected(0);
		});
    }
}

