							fld_class_OnValueChanged(strValue)
{
	// TODO: Add your event handler code here
	app._slf(_swdoc.MF().sl_slarules, "callclass="+app.pfu(strValue));
	_swdoc.MF().sl_slarules.Refresh();

	app._een(_swdoc.MF().btn_add_rule, (strValue != ""));
	app._een(_swdoc.MF().btn_del_rule, (strValue != ""));
	app._een(_swdoc.MF().btn_up_rule, (strValue != ""));
	app._een(_swdoc.MF().btn_down_rule, (strValue != ""));

	app._een(_swdoc.MF().lbl_sla, (strValue != ""));
	app._een(_swdoc.MF().fld_sla, (strValue != ""));

	app._een(_swdoc.MF().flg_use_rules, (strValue != ""));

	if(strValue != "")
	{
    	var oParams = {};
    	oParams.callclass = strValue;

    	//-- call script
    	var resSLA = app.g.submitsqp("form/manage_slas/get_class_default_sla",oParams);
		app._ete(_swdoc.MF().fld_sla, resSLA);
		_swdoc.strClassDefSLA = resSLA;

		var resPriority = app.g.submitsqp("form/manage_slas/get_class_default_priority",oParams);
		app._ete(_swdoc.MF().fld_priority, resPriority);
		_swdoc.strClassDefPriority = resPriority;

		var resUseSLARules = app.g.submitsqp("form/manage_slas/get_class_use_sla_rules",oParams);
		app._eva(_swdoc.MF().flg_use_rules, resUseSLARules);

		if(resSLA != "")
		{
			var oRecSLD = app.g.get_sqrecordset("general/get_itsm_sla","kv="+resSLA);
			if(oRecSLD.Fetch())
			{
				var intSLAID = app.g.get_field(oRecSLD, "pk_slad_id");
				app._slf(_swdoc.MF().fld_priority, "_swc_fk_slad="+intSLAID+"&_swc_flg_sla=1");
			}
		}
		app._een(_swdoc.MF().lbl_priority, (resSLA != ""));
		app._een(_swdoc.MF().fld_priority, (resSLA != ""));			
    }
    else
    {
    	app._ete(_swdoc.MF().fld_sla, "");
    	_swdoc.strClassDefSLA = "";
    }
}

							fld_sla_OnValueChanged(strValue)
{
	// TODO: Add your event handler code here
	app._eva(_swdoc.MF().fld_priority , "");

	if(strValue!="")
	{
  		var oRecSLD = app.g.get_sqrecordset("general/get_itsm_sla","kv="+strValue);
		if(oRecSLD.Fetch())
		{
			var intSLAID = app.g.get_field(oRecSLD, "pk_slad_id");
			app._slf(_swdoc.MF().fld_priority, "_swc_fk_slad="+intSLAID+"&_swc_flg_sla=1");

			var strParams = "callclass="+app.pfu(_swdoc.MF().fld_class.value)+"&slaid="+app.pfu(intSLAID)+"&slaname="+strValue;
			var res = app.g.submitsqp("form/manage_slas/update_class_default_sla", strParams);
			if(res) _swdoc.strClassDefSLA = res;
		}
	}
	else
	{
		var strConfirm = "This will clear the current Default SLA for Call Class "+_swdoc.MF().fld_class.value+". Do you want to continue?";
		if(confirm(strConfirm))
    	{
			var strParams = "callclass="+app.pfu(_swdoc.MF().fld_class.value)+"&slaid=&slaname=";
			if(app.g.submitsqs("form/manage_slas/update_class_default_sla", strParams))
			{
				app._ete(_swdoc.MF().fld_sla, "");
				 _swdoc.strClassDefSLA = "";
			}
		}
		else
		{
			app._ete(_swdoc.MF().fld_sla, _swdoc.strClassDefSLA);
		}
	}

	var boolEnable = strValue!="";
	app._een(_swdoc.MF().lbl_priority, boolEnable);
	app._een(_swdoc.MF().fld_priority, boolEnable);
}

