							btn_add_rule_OnPressed()
{
   	var intCurrCount = sl_slarules.rowCount();
	intCurrCount++;
	//-- create a new stage for current workflow
	var strURL = "seq=" + intCurrCount + "&callclass=" + _swdoc.MF().fld_class.value;
	app.OpenFormForAdd("tf_sla_rule", "", strURL, true, function()
	{
		//-- refresh
		sl_slarules.Refresh();
	});
}

							btn_del_rule_OnPressed()
{
	// TODO: Add your event handler code here
	//-- delete the selected rule

	var selectedRow     = sl_slarules.curSel;
	var strConfirm = "Are you sure you want to delete the selected rule?";
	var intStageId = sl_slarules.GetItemTextRaw(sl_slarules.curSel, "pk_auto_id");
	var delSequence = sl_slarules.GetItemTextRaw(sl_slarules.curSel, "seq");

    if(intStageId!="")
    {
    	if(app.g.sl_deldbrow(sl_slarules,selectedRow,"pk_auto_id",null,strConfirm))
		{
			_swdoc.MF().sl_slarules.Refresh();
			//-- loop through list and set seq for each to #
			var intTask=0;
			for(var x=0; x < sl_slarules.rowCount();x++)
			{
				var thisSeq = sl_slarules.GetItemTextRaw(x, "seq");
				if(thisSeq>delSequence)
				{
					intStage = sl_slarules.GetItemTextRaw(x, "pk_auto_id");

					var strParams = "sid="+app.pfu(intStage);
					app.g.submitsqs("form/manage_slas/dec_rule_seq", strParams);
				}
			}
			sl_slarules.Refresh();		
			sl_slarules.SetRowSelected(0, true, false);				
		}
	}


}

							btn_up_rule_OnPressed()
{
	// TODO: Add your event handler code here

	//-- get selected rule id to move up
	var currRow = sl_slarules.curSel;
	var intUpStageID = sl_slarules.GetItemTextRaw(sl_slarules.curSel, 'pk_auto_id');
	var intUpStageSeq = sl_slarules.GetItemTextRaw(sl_slarules.curSel, 'seq');	

	//-- get rule id to move down
	var intDownStageID = sl_slarules.GetItemTextRaw(sl_slarules.curSel-1, 'pk_auto_id');
	var intDownStageSeq = sl_slarules.GetItemTextRaw(sl_slarules.curSel-1, 'seq');

	//-- update seq id for both stages
	var strParams = "sid="+app.pfu(intUpStageID);
	if(app.g.submitsqs("form/manage_slas/dec_rule_seq", strParams))
	{
		var strParams = "sid="+app.pfu(intDownStageID);
		if(app.g.submitsqs("form/manage_slas/inc_rule_seq", strParams))
		{
			//-- refresh list
			sl_slarules.Refresh();
			sl_slarules.SetRowSelected(currRow-1, true, false);
		}
	}
}

							btn_down_rule_OnPressed()
{
	// TODO: Add your event handler code here

	//-- get selected stage id to move down
	var currRow = sl_slarules.curSel;
	var intDownStageID = sl_slarules.GetItemTextRaw(sl_slarules.curSel, 'pk_auto_id');
	var intDownStageSeq = sl_slarules.GetItemTextRaw(sl_slarules.curSel, 'seq');	

	//-- get stage id to move down
	var intUpStageID = sl_slarules.GetItemTextRaw(sl_slarules.curSel+1, 'pk_auto_id');
	var intUpStageSeq = sl_slarules.GetItemTextRaw(sl_slarules.curSel+1, 'seq');

	//-- update seq id for both stages
	var strParams = "sid="+app.pfu(intUpStageID);
	if(app.g.submitsqs("form/manage_slas/dec_rule_seq", strParams))
	{
		var strParams = "sid="+app.pfu(intDownStageID);
		if(app.g.submitsqs("form/manage_slas/inc_rule_seq", strParams))
		{
			//-- refresh list
			sl_slarules.Refresh();
			sl_slarules.SetRowSelected(currRow+1, true, false);
		}
	}
}

							sl_slarules_OnItemSelected(nRow)
{

	//-- disable up button if first row selected
	if (nRow == 0)
	{
		app._een(_swdoc.MF().btn_up_rule,false);
	}
	else
	{
		app._een(_swdoc.MF().btn_up_rule,true);
	}

	//-- disable down button if last row selected
	if (nRow == (_swdoc.MF().sl_slarules.rowCount() - 1))
	{
		app._een(_swdoc.MF().btn_down_rule,false);
	}
	else
	{
		app._een(_swdoc.MF().btn_down_rule,true);
	}

}

							flg_use_rules_OnValueChanged(strValue)
{
	var strParams = "callclass="+app.pfu(_swdoc.MF().fld_class.value)+"&use_sla_rules="+strValue;
	app.g.submitsqp("form/manage_slas/update_class_use_sla_rules", strParams);
}
