btn_search_OnPressed()
{
	//-- 19.01.2006 - NWJ
	//-- create search criteria

	app._een(btn_select, false);
	app.g.sqllist_clear(sl_results);

	var boolAND = (lb_type.value==1);
	var strFilter = app.g.create_search_params(mainform,tb_table.text,"",boolAND,false);
	var strCalls = "";

	if(sl_configitems1.rowCount()>0)
	{
		var strKeys = app.g.sqllist_get_valuesdel(sl_configitems1, 'pk_auto_id', ",", "");	
		var strParams = "keys="+app.pfu(strKeys);
		var oRS = app.g.get_sqrecordset("cmdb/select_calls_related_to_cis",strParams);
		while(oRS.Fetch())
		{
			var currKey = app.g.get_field(oRS,"FK_CALLREF");
			if (strCalls!="")strCalls +=",";
			strCalls += app.g.pfs(currKey);
		}
	}

	if(sl_services1.rowCount()>0)
	{
		var strKeys = app.g.sqllist_get_valuesdel(sl_services1, 'pk_auto_id', ",", "");	
		var strParams = "keys="+app.pfu(strKeys);
		var oRS = app.g.get_sqrecordset("cmdb/select_calls_related_to_cis",strParams);
		while(oRS.Fetch())
		{
			var currKey = app.g.get_field(oRS,"FK_CALLREF");
			if (strCalls!="")strCalls +=",";
			strCalls += app.g.pfs(currKey);
		}
	}

	if(strCalls!="")
	{
		if(strFilter!="")
			strFilter += "&";
		strFilter += "cr="+strCalls;
	}

	//-- check if user selected logged after or before dates
	var strDateFilter = "";
	if(tb_lda.value>"")
	{
		if(strFilter!="") strFilter += "&";
		strFilter += "lda=" + tb_lda.value;
	}

	if(tb_ldb.value>"")
	{
		if(strFilter!="") strFilter += "&";
		strFilter += "ldb=" + tb_ldb.value;
	}	

	//-- now do an or search on the keyword fields (field 1 = to 2 , 3 ,4 ,5 and 6
	var strKeySearch = "";
	for (var x=1;x<=6;x++)
	{
		var strKeySearchValue = mainform.elements["tb_keys_" + x].text;		
		if(strKeySearchValue!="")
		{
			if (strFilter != "") strFilter += "&";			
			strFilter += "kw"+x+"="+app.pfu(strKeySearchValue);
		}
	}

	//-- apply quick filter
	switch(lb_qcall.value)
	{
		case "1":
			//-- we want to filter out resolved calls 
			if (strFilter != "") strFilter += "&";			
			strFilter += "bres=0";
			break;
		case "2":
			//-- include resolved
			if (strFilter != "") strFilter += "&";			
			strFilter += "bres=1";
			break;
	}

	//--MessageBox(strFilter);

	//-- apply filter
	app.g.sl_filter(sl_results,strFilter,null,0);
	sl_results.Refresh();
}

