btn_search_cci1_OnPressed()
{
    //-- NWJ 08.03.2004
    //-- open CI search form in select mode
	var strURL = ""
	app.cmdb.search_ci(true,strURL,function(sResults)
	{
		if (sResults.selectedkeys=="") return;

		var strServiceKeys = app.g.sl_get_valuesdel(sl_services1,"pk_auto_id",",","",true);
		if(strServiceKeys!="")
			strServiceKeys += ",";
		strServiceKeys += sResults.selectedkeys;

		var strCIKeys = app.g.sl_get_valuesdel(sl_configitems1,"pk_auto_id",",","",true);
		if(strCIKeys!="")
			strCIKeys += ",";
		strCIKeys += sResults.selectedkeys;

 		app._slf(sl_services1, "pids="+strServiceKeys+"&sf=cis_servicesonly");
 		app._slf(sl_configitems1, "pids="+strCIKeys+"&sf=cis_excludeservice");
		sl_services1.Refresh();
 		sl_configitems1.Refresh();
	});
}

