function setup_form_fields()
{
	_swdoc.setup_fixed_criteria();

	//-- set field states
	app._een(_swdoc.MF().btn_select, false);
	app._een(_swdoc.MF().btn_subset, false);
	app._een(_swdoc.MF().btn_remsub, false);
	app._een(_swdoc.MF().btn_subclear, false);


	app._een(_swdoc.MF().btn_delete, false);
	app._een(_swdoc.MF().btn_view, false);
	_swdoc.currentList = _swdoc.MF().sl_results;

	//-- show hide buttons depending on searchmode
	_swdoc.boolSearchMode = (_swdoc.GetArg("searchmode")=="1");

	app._evi(_swdoc.MF().btn_delete, (!_swdoc.boolSearchMode));
	app._evi(_swdoc.MF().btn_view, (!_swdoc.boolSearchMode));
	app._evi(_swdoc.MF().btn_select, (_swdoc.boolSearchMode));
	app._evi(_swdoc.MF().btn_cancel, (_swdoc.boolSearchMode));	

	if(!_swdoc.boolSearchMode)_swdoc.SetTitle("Manage Customer Records");

	if(_swdoc.GetArg('searchtype')=="1")
	{
		app._eva(_swdoc.MF().lb_type, 1);
	}

	if(_swdoc.GetArg('autorun')=="1")
	{
		_swdoc.MF().btn_search_OnPressed();
	}

	if(app.g.remote_support_enabled())
	{
		app._evi(_swdoc.MF().btn_remotesupport, true);
	}
}

//-- NWJ - 11.08.2008 - read in url and find any matching bindings - if found set field value and disable
//-- as means we have passed in fixed searc criteria - also returns fixed filter
function setup_fixed_criteria(strFilter)
{
	var boolCanChange = (_swdoc.GetArg('canreset')=="1");
	var strFilter = "";
	for(var x = 0; x < _swdoc.MF().elements.length;x++)
	{
		var element = _swdoc.MF().elements[x];
		if((element.dataRef!="") && ((element.type == FE_FIELD) || (element.type == app.FE_SEARCHFIELD)))
		{
			var arrInfo = element.dataRef.split(".");
			var strFixedValue = _swdoc.GetArg(arrInfo[1]);
			//-- START 107620
   			strFixedValue=strFixedValue.replace("% ","%");
			//-- END 107620
			if(strFixedValue!="")
			{
				app._ete(element, strFixedValue);
				app._een(element, boolCanChange);

				var colValue = app.g.encapsulate(arrInfo[0],arrInfo[1],strFixedValue);

				if(strFilter!="")strFilter += "&";
				strFilter += element.dataRef + "=" + colValue;

			}

			//-- F0098488
			//-- If appcode filter is set then apply it to the organisation 
			if((arrInfo[1]=="fk_company_id") && (dd.GetGlobalParamAsNumber("Application Settings/UseAppCodeFilter")==1))
				app._slf(element , "_swc_appcode IN("+app.itsm.get_session_param("datasetFilterList")+")");	
		}

	}

	return strFilter;
}

							btn_search_OnPressed()
{
	MessageBox("pressed");
	//-- 19.01.2006 - NWJ
	//-- create search criteria
	_swdoc.strFilter = ""; 
	var intPageNo = 1;
	_swdoc.intPageNo = 1;

	app._een(btn_select, false);
	app.g.sqllist_clear(_swdoc.MF().sl_results);

	var boolAND = (lb_type.value==1);
	/*var strFilter = app.g.create_search_params(mainform,tb_table.text,"",boolAND,false);*/
	_swdoc.strFilter="_kv="+Field1.text+"&lid=ARCH%20Topic%20Countries";

	//-- apply filter

	//-- switch to results tab
	app._etab(TabRes, 0);


	//###20120716 app.g.sl_filter(sl_results,_swdoc.strFilter,sl_results_OnItemSelected,0);

	//###20120716

	//--Update Document so Filter is Global
	_swdoc.strFilter = _swdoc.strFilter;

	//-- Add Paging

	/*_swdoc.strFilter += "&paged=true&start="+intPageNo+"&end=&webclient="+_swdoc.webclient+"&multi_org="+app.pfu(_swdoc.MF().fld_company.value)+"&multi_site="+app.pfu(_swdoc.MF().fld_site.value);
	//strFilter +="&multi_org="+app.pfu(_swdoc.MF().fld_company.value)+"&multi_site="+app.pfu(_swdoc.MF().fld_site.value);*/
	app.g.sl_filter(_swdoc.MF().sl_results,_swdoc.strFilter,_swdoc.MF().sl_results_OnItemSelected,0);
	//###20120716

	if(_swdoc.GetArg('autorun')==1 && _swdoc.boolSearched)
	{
		_swdoc.boolSearched = false;
		if(_swdoc.MF().sl_results.rowCount()==1){
			//###20120718
			_swdoc.MF().sl_results.SetRowSelected(0);
    		_swdoc.MF().sl_results_OnItemSelected(0);
			_swdoc.MF().btn_select_OnPressed();
			_swdoc.MF().sl_results.Refresh();
		}
	} 
	_swdoc.MF().sl_results.Refresh();

	//Show Next Button
	if(sl_results.rowCount() < _swdoc.intPerPage)
	{
		app._een(_swdoc.MF().btn_next,false);	
	}else
	{
		app._een(_swdoc.MF().btn_next,true);	
	}
	app._een(_swdoc.MF().btn_prev,false);

}

							btn_clear_OnPressed()
{
	//-- clear search fields
	//MessageBox(tb_table.text + " : " + tb_table.value)
	app.g.clear_form_fields(tb_table.text,mainform,"",false);
	_swdoc.setup_form_fields();
	app._slf(_swdoc.MF().sl_results , "empty=1");
	_swdoc.MF().sl_results.Refresh();

}

