							btn_subclear_OnPressed()
{
	var strValue = app.g.dd_isnumeric(tb_table.text,tb_keycolname.text)?"-1":"''";	
	var strFilter = "";//tb_keycolname.text + " = " + strValue;
	app.g.sl_filter(sl_sub,strFilter,null,0);
	sl_sub_OnItemSelected(0);

}

							btn_remsub_OnPressed()
{
	app.g.sl_clearselected(sl_sub,null,0);

	//-- reset filter
	var strQuote = "";//app.g.dd_isnumeric(tb_table.text,tb_keycolname.text)?"":"'";
	var strSubKeys= app.g.sl_get_valuesdel(sl_sub,0,",",strQuote);
	if((strQuote=="") && (strSubKeys=="")) 
	{
		strSubKeys ="-1";	
	}
	else if(strSubKeys=="")
	{
		strSubKeys ="'-1'";
	}
	var strFilter = "_kv=" + strSubKeys;
	app.g.sl_filter(sl_sub,strFilter,null,0);
	sl_sub_OnItemSelected(0);	

}

