}

							btn_subset_OnPressed()
{
	//-- add selected items to the subset list
	var strQuote = "";//app.g.dd_isnumeric(tb_table.text,tb_keycolname.text)?"":"'";
	var strSelKeys = app.g.sl_getselected_valuesdel(sl_results	,tb_keycol.text,",",strQuote,true);
	var strSubKeys = app.g.sl_get_valuesdel(sl_sub,tb_keycol.text,",",strQuote,true);

	//-- if we already have sub calls
	if(strSubKeys!="")
	{
		strSelKeys += "," + strSubKeys;
	}


	var strFilter = "_kv=" + strSelKeys;
	app.g.sl_filter(sl_sub,strFilter,null,0);


}

