							btn_delete_OnPressed()
{
	var strConfirm = "Are you sure you want to delete the selected records?";
	if(app.g.sl_deldbrows(_swdoc.currentList,'keysearch',null,strConfirm))
	{
		//-- delete userdb rating - training etc done in vpme table script
		_swdoc.strFilter = ""; 
	var intPageNo = 1;
	_swdoc.intPageNo = 1;

	app._een(btn_select, false);
	app.g.sqllist_clear(_swdoc.MF().sl_results);

	var boolAND = (lb_type.value==1);
	var strFilter = app.g.create_search_params(mainform,tb_table.text,"",boolAND,false);

	//-- apply filter

	//-- switch to results tab
	app._etab(TabRes, 0);


	//###20120716 app.g.sl_filter(sl_results,strFilter,sl_results_OnItemSelected,0);

	//###20120716

	//--Update Document so Filter is Global
	_swdoc.strFilter = strFilter;

	//-- Add Paging

	strFilter += "&paged=true&start="+intPageNo+"&end=&webclient="+_swdoc.webclient+"&multi_org="+app.pfu(_swdoc.MF().fld_company.value)+"&multi_site="+app.pfu(_swdoc.MF().fld_site.value);
	//strFilter +="&multi_org="+app.pfu(_swdoc.MF().fld_company.value)+"&multi_site="+app.pfu(_swdoc.MF().fld_site.value);
	app.g.sl_filter(_swdoc.MF().sl_results,strFilter,_swdoc.MF().sl_results_OnItemSelected,0);
	//###20120716

	_swdoc.MF().sl_results.Refresh();

	//Show Next Button
	if(sl_results.rowCount() < _swdoc.intPerPage)
	{
		app._een(_swdoc.MF().btn_next,false);	
	}else
	{
		app._een(_swdoc.MF().btn_next,true);	
	}
	app._een(_swdoc.MF().btn_prev,false);	
	}

}

							btn_view_OnPressed()
