}

							btn_remotesupport_OnPressed()
{
	//-- Attempt to launch a pinned support session
	var strEmail = app.g.sl_getrawcolvalue(_swdoc.currentList,-1,"email");
	app.g.start_remote_support(strEmail);
}

							btn_next_OnPressed()
{
	_swdoc.intPageNo++;

	//-- Add Paging
	var strFilter = _swdoc.strFilter;
	strFilter += "&paged=true&start="+_swdoc.intPageNo+"&end=&webclient="+_swdoc.webclient+"&multi_org="+app.pfu(_swdoc.MF().fld_company.value)+"&multi_site="+app.pfu(_swdoc.MF().fld_site.value);

	//-- apply filter
	app.g.sl_filter(sl_results,strFilter,null,0)
	_swdoc.MF().sl_results.Refresh();

	if(sl_results.rowCount() < _swdoc.intPerPage)
	{
		app._een(_swdoc.MF().btn_next,false);	
	}
	app._een(_swdoc.MF().btn_prev,true);
}

							btn_prev_OnPressed()
{
	_swdoc.intPageNo--;

	//-- Add Paging
	var strFilter = _swdoc.strFilter;
	strFilter += "&paged=true&start="+_swdoc.intPageNo+"&end=&webclient="+_swdoc.webclient+"&multi_org="+app.pfu(_swdoc.MF().fld_company.value)+"&multi_site="+app.pfu(_swdoc.MF().fld_site.value);

	//-- apply filter
	app.g.sl_filter(sl_results,strFilter,null,0)
	_swdoc.MF().sl_results.Refresh();

	if (_swdoc.intPageNo == 1) 
	{
		app._een(_swdoc.MF().btn_prev,false);
	}
	app._een(_swdoc.MF().btn_next,true);

}
