method.code:
						var boolFigureChanged = false;
var origValue = "";

function MF()
{
	return mainform;
}

function ServSubs()
{
	return _swdoc.sc_subscription;
}


function setup_form_fields()
{
	_swdoc.refresh_service_levels();
	var boolAllowEdit = _swdoc.MF().fld_chargable.value==1;

	app._een(_swdoc.MF().fld_unit_charge,boolAllowEdit);
	app._een(_swdoc.MF().fld_mark_up,boolAllowEdit);
	app._een(_swdoc.MF().fld_units,boolAllowEdit);
	_swdoc.origValue = sc_subscription.request_price;
}

function recalculate()
{
	var strRev = parseFloat(_swdoc.ServSubs().unit_charge)*parseFloat(_swdoc.ServSubs().units)*((100+parseFloat(_swdoc.ServSubs().mark_up))/100);
	strRev = String(strRev);
	var strIndex = strRev.indexOf(".",0);
	if(strIndex>-1){
		var strCharge = strRev.substr(0, strIndex+3);

		if((strIndex+3)-strCharge.length)
			strCharge += "0";
		_swdoc.ServSubs().total_charge = strCharge;
	}else
	{
		_swdoc.ServSubs().total_charge = strRev+".00";
	}

	_swdoc.UpdateFormFromData();
}

function refresh_service_levels()
{
	var strFilter = "swc=FK_SUBSCRIPTION&kvs="+_swdoc.ServSubs().pk_id;
	var strAppcodes = app.oSettings.get_setting("strFilterAppcodeSLAEntities");
	if(strAppcodes!="" && strAppcodes!=false)
		strFilter+="&_acin="+strAppcodes;
	app._slf(_swdoc.MF().sl_service_sla2,strFilter);
	_swdoc.MF().sl_service_sla2.Refresh();

	var x = app.g.sqllist_get_valuesdel(_swdoc.MF().sl_service_sla2, "fk_sla", ",", "'");
	var strFilter = "swc=fk_service&kvs="+_swdoc.ServSubs().fk_service+"&_exc=fk_sla&_exv="+x;
	if(strAppcodes!="" && strAppcodes!=false)
		strFilter+="&_acin="+strAppcodes;
	app._slf(_swdoc.MF().sl_service_sla1,strFilter);
	_swdoc.MF().sl_service_sla1.Refresh();

}

						OnDataSaved()
