	// TODO: Add your event handler code here
	_swdoc.boolFigureChanged = true;

}

						OnSaveData()
{
	// TODO: Add your event handler code here
	if(!app.service.can_update(true))
		return false;

	var x = app.g.get_record("sc_folio", sc_subscription.fk_service);
	if(parseFloat(x.cost_request)>parseFloat(sc_subscription.request_price))
	{
		var boolContinue = confirm('The price for this customer is less than the total cost of providing the request.\nWould you like to continue?');
		if(boolContinue==false)
			return false;
	}
	return true;
}

