							sl_service_sla2_OnItemSelected(nRow)
{
	// TODO: Add your event handler code here

	var boolEnable = (sl_service_sla2.rowCount() > 0);
	var selCount = app.g.sl_getselected_count(sl_service_sla2);
	app._een(btn_view_sla,(selCount==1));
	app._een(btn_del_sla,boolEnable);



}

							btn_add_sla_OnPressed()
{
	// TODO: Add your event handler code here
	if(sl_service_sla1.curSel<0)
	{
		app._een(btn_add_sla,false);
		return true;
	}
	if(!app.service.can_update(true))
	{
		_swdoc.MF().sl_service_sla1.Refresh();
		_swdoc.checkSLAs();
		_swdoc.MF().sl_service_sla2.Refresh();
		return false;
	}

	var strSLAName = app.g.sl_getcolvalue(sl_service_sla1, sl_service_sla1.curSel , "fk_sla_name");
	var strSLA = app.g.sl_getcolvalue(sl_service_sla1, sl_service_sla1.curSel , "fk_sla");
	var strCost = app.g.sl_getcolvalue(sl_service_sla1, sl_service_sla1.curSel, "cost");
	var strPrice = app.g.sl_getcolvalue(sl_service_sla1, sl_service_sla1.curSel, "price");
	var strAppcode = app.g.sl_getcolvalue(sl_service_sla1, sl_service_sla1.curSel, "appcode");

	var aP = {};
	aP.sid=_swdoc.ServSubs().pk_id;
	aP.sla=strSLA;
	aP.slaname=strSLAName;
	aP.cost=strCost;
	aP.price=strPrice;
	app.g.submitsqs("service/process/assign_sla_to_subscription.insert", aP);

	_swdoc.refresh_service_levels();

	app._een(btn_add_sla,false);	
}

							btn_del_sla_OnPressed()
{
	// TODO: Add your event handler code here
	if(!app.service.can_update(true))
	{
		_swdoc.MF().sl_service_sla1.Refresh();
		_swdoc.checkSLAs();
		_swdoc.MF().sl_service_sla2.Refresh();
		return false;
	}

	var strSLA = app.g.sl_getcolvalue(sl_service_sla2, sl_service_sla2.curSel, "fk_sla");
	if(strSLA == sc_subscription.fk_default_sla)
	{
			MessageBox("Cannot remove the selected Service Level.\nPlease alter the Default Service Level of this subscription before removing.");
	}
	else
	{
		var strConfirm = "Are you sure you want to remove the selected service level?";
		if(confirm(strConfirm))
		{
			var aP = {};
			aP.sid=_swdoc.ServSubs().pk_id;
			aP.sla=strSLA;
			app.g.submitsqs("service/process/remove_sla_from_subscription.delete", aP);
		}
	}

	_swdoc.refresh_service_levels();
	app._een(btn_del_sla,false);
	app._een(btn_view_sla,false);


}

							btn_view_sla_OnPressed()
{
	// TODO: Add your event handler code here
	if(sl_service_sla2.curSel<0)
		return false;
	sl_service_sla2.EditRecord(sl_service_sla2.curSel)

}

							Field3_OnValueChanged(strValue)
{
	// TODO: Add your event handler code here

	sc_subscription.subscription_price = app.format_float_to_decimal_str(strValue);
	_swdoc.UpdateFormFromData();

}

							Field4_OnValueChanged(strValue)
{
	// TODO: Add your event handler code here
	if(isNaN(strValue))
	{
		sc_subscription.request_price = _swdoc.origValue;
	}
	else
	{
		sc_subscription.request_price = app.format_float_to_decimal_str(strValue);
		_swdoc.origValue = sc_subscription.request_price;
	}
	_swdoc.UpdateFormFromData();


}

							btn_rem_class_OnPressed()
{
   	var selectedRow     = sl_defslas.curSel;
    var strConfirm		= "Are you sure you want to remove this associated SLA/Call Class?";

	if(app.g.sl_deldbrow(sl_defslas,selectedRow,"pk_auto_id",null,strConfirm))
	{
		_swdoc.MF().sl_defslas.Refresh();
	}
}

							btn_add_class_OnPressed()
{
    var strURL = "reltype=SUBS&fk_rel_id=" + app.pfu(sc_subscription.pk_id);
    app.OpenFormForAdd("tf_rel_sla_class" ,"", strURL, true, function()
    {
    	_swdoc.MF().sl_defslas.Refresh();
    });  	
}

							Tab1_OnTabSelected(nTab)
{
	if(nTab == 2)
