// DTH: F0097669 KB Cat Rights
// Alter form components based on rights
function setup_based_on_rights()
{
	app._een(_swdoc.MF().s_kb__docvisibletocustomers1, (_swdoc.arrKbCatRights["visible"] == "1")); // Can alter the Visible Status

	if ((_swdoc.arrKbCatRights["composed"] != "1") && (_swdoc.arrKbCatRights["waiting"] != "1") && (_swdoc.arrKbCatRights["approved"] != "1") && (_swdoc.arrKbCatRights["published"] != "1") && (_swdoc.arrKbCatRights["retired"] != "1"))
	{
		// No rights to any options relating to Status
		app._een(_swdoc.MF().s_kb__docstatus, false);
	}
	else
	{
		var strItems = "";
		var strName = "";
		if ((_swdoc.arrKbCatRights["composed"] == "1") || (_swdoc.swkb().docstatus == 0))
		{
			if(strItems!="") strItems+="|";

			strName = "Document Being Composed";
			strName = strName.replace(/&apos;/g, "'");
			strItems += strName+"^"+"0";		
		}

		if ((_swdoc.arrKbCatRights["waiting"] == "1")|| _swdoc.swkb().docstatus==1  )
		{
			if(strItems!="") strItems+="|";

			strName = "Document Awaiting Approval";
			strName = strName.replace(/&apos;/g, "'");
			strItems += strName+"^"+"1";		
		}

		if ((_swdoc.arrKbCatRights["approved"] == "1")|| _swdoc.swkb().docstatus==2  )
		{
			if(strItems!="") strItems+="|";

			strName = "Document Approved for Publishing";
			strName = strName.replace(/&apos;/g, "'");
			strItems += strName+"^"+"2";		
		}

		if ((_swdoc.arrKbCatRights["published"] == "1")|| _swdoc.swkb().docstatus==4  )
		{
			if(strItems!="") strItems+="|";

			strName = "Document Published";
			strName = strName.replace(/&apos;/g, "'");
			strItems += strName+"^"+"4";		
		}

		if ((_swdoc.arrKbCatRights["retired"] == "1") || _swdoc.swkb().docstatus==5  )
		{
			if(strItems!="") strItems+="|";

			strName = "Document Retired";
			strName = strName.replace(/&apos;/g, "'");
			strItems += strName+"^"+"5";		
		}

		_swdoc.MF().s_kb__docstatus.pickList = strItems;
	}

	app._een(_swdoc.MF().btn_search_ci, (_swdoc.arrKbCatRights["changecis"] == "1")); 

	app._een(_swdoc.MF().s_kb__fk_bpm_assigngrp, (_swdoc.arrKbCatRights["changesettings"] == "1")); 
	app._een(_swdoc.MF().s_kb__fk_bpm_process, (_swdoc.arrKbCatRights["changesettings"] == "1")); 
	app._een(_swdoc.MF().Field1, (_swdoc.arrKbCatRights["changesettings"] == "1")); 
	app._een(_swdoc.MF().Field2, (_swdoc.arrKbCatRights["changesettings"] == "1")); 

}

//-- get list of kb catalogs
function get_catalog_list()
{
	var xmlmc = new XmlMethodCall;
	if(xmlmc.Invoke("knowledgebase", "catalogList"))
	{
	   	//-- get the result
		var strXML = (app.bWebClient)? xmlmc._lastresult:xmlmc.GetReturnXml();
		var objRes = app.XMCResult(strXML);
		if(!objRes.success)
		{
	        MessageBox("Failed to load catalogs (" + objRes.message + "). Please contact your Supportworks Supervisor.");
	    }	
	    else
	    {
	    	//-- create xmldom from returned xml
			var myXmlFile = new XmlFile(); 
			bRet = myXmlFile.loadFromString(strXML); 

			var strItems = "";
			oRec = new Object();
			for (count = 1; count < myXmlFile.methodCallResult.data.length; count ++) 
			{  
				var strCatId = myXmlFile.methodCallResult.data[count]['catalogId'].nodeValue;
				var strName = myXmlFile.methodCallResult.data[count]['name'].nodeValue;
				if(strItems!="")strItems+="|";
				strName = strName.replace(/&apos;/g, "'");
				strItems += strName+"^"+strCatId;		
			}
			_swdoc.MF().s_kb__catalogid.pickList = strItems;
	    }
    }
    else
    {
    	MessageBox("Failed to invoke knowledgebase.catalogList. " + xmlmc.GetLastError() + ". Please contact your Supportworks Supervisor.");
    }
}


//-- add new or update kbase article
function swkb_addarticle()
{  		
  	var xmlmc = new XmlMethodCall;

	//-- mandatory - can be blank
	xmlmc.SetValue("docRef", _swdoc.swkb().docref);		

	//-- optional but must be sent
	xmlmc.SetValue("title", _swdoc.swkb().title);
	xmlmc.SetValue("docVisibleToCustomers", (_swdoc.swkb().docflags==1));		
	xmlmc.SetValue("docStatus", _swdoc.swkb().docstatus);
	xmlmc.SetValue("catalogId", _swdoc.swkb().catalog);		
	xmlmc.SetValue("problem", _swdoc.swkb().problem);		
	xmlmc.SetValue("solution", _swdoc.swkb().solution);		
	xmlmc.SetValue("author", _swdoc.swkb().author);//MF().s_kb__author.value);	
	xmlmc.SetValue("callref", _swdoc.swkb().callref);	
	xmlmc.SetValue("callProbCode", _swdoc.swkb().callprobcode);	

	var strDate ="";
	if(_swdoc.swkb().docdatex!="")
	{
		var strDate = app.global.GetAsISO8601TimeString(_swdoc.MF().s_kb__docdate.value);
		xmlmc.SetValue("docDate", strDate);//MF().tb_date.text);
	}

	xmlmc.SetValue("keywords", _swdoc.swkb().keywords);
	xmlmc.SetValue("template", _swdoc.swkb().template);			
	xmlmc.SetValue("ownerAnalystID", _swdoc.swkb().owneranalystid);		
	xmlmc.SetValue("ownerGroupID", _swdoc.swkb().ownergroupid);		

	if(_swdoc.swkb().expirydatex!="")
	{
		var strDate = app.global.GetAsISO8601TimeString(_swdoc.MF().s_kb__expirydate.value);
		xmlmc.SetValue("expiryDate", strDate);//MF().tb_date.text);
	}

	xmlmc.SetValue("isRetired", (_swdoc.swkb().isretired==1));

	var strAction = (_swdoc.boolInserting)? "articleAdd":"articleUpdate";

	if(xmlmc.Invoke("knowledgebase", strAction))
	{
	    //-- get the result
		var strXML = (app.bWebClient)? xmlmc._lastresult:xmlmc.GetReturnXml();
		var objRes = app.XMCResult(strXML);
		if(!objRes.success)
		{
	        MessageBox("Failed to process KnowledgeBase article (" + objRes.message + "). Please contact your Supportworks Supervisor.");
	    }	
	    else
	    {
	    	if(_swdoc.boolInserting)
	    	{
	    		var strNewDocRef = xmlmc.GetReturnValue("docRef");
				_swdoc.swkb().docref = strNewDocRef;
			}
			return true;
	    }
    }
    else
    {
    	if(xmlmc.GetLastError()=="Article with docRef["+_swdoc.swkb().docref+"] does not exist." && strAction=="articleUpdate")
    	{
    		return true;
    	}
    	else
    		MessageBox("Failed to process Knowledgebase article. " + xmlmc.GetLastError() + ". Please contact your Supportworks Supervisor.");
    }		

    return false;
}

function upload_external_document()
{
	//-- we have selected a file
	if(_swdoc.MF().fl_file.rowCount()>0)
	{
	 	var xmlmc = new XmlMethodCall;

		//-- mandatory - can be blank
		xmlmc.SetValue("docRef", _swdoc.swkb().docref);		

		var strFileName = _swdoc.MF().fl_file.GetItemText(_swdoc.MF().fl_file.rowCount()-1, 0);
		var strData = app.global.LoadFileInBase64(_swdoc.swkb().sourcepath);

		var strXML = "<fileName>"+strFileName+ "</fileName><fileData>"+strData+"</fileData>";
		xmlmc.SetParamAsComplexType("document",strXML);	

		if(xmlmc.Invoke("knowledgebase", "documentUpdate"))
		{
		    //-- get the result
			var strXML = (app.bWebClient)? xmlmc._lastresult:xmlmc.GetReturnXml();
			var objRes = app.XMCResult(strXML);
			if(!objRes.success)
			{
		        MessageBox("Failed to upload the associated external document (" + objRes.message + "). Please contact your Supportworks Supervisor.");
		    }		
		}
	}
}

						OnSaveData()
{
	//-- Save Last Update Modified Datex
	_swdoc.swkb().lastmodifieddatex = app.g.todaysdate_epoch();
	for(var x=0; x < mainform.elements.length;x++)
	{
		if (mainform.elements[x].dataRef)
		{
			if (mainform.elements[x].dataRef!="swkb_articles.docref")
			{
				if(mainform.elements[x].mandatory)
				{
					var strField = mainform.elements[x].dataRef;
					var arrField = strField.split(".");
					if(mainform.elements[x].value=="")
					{
						MessageBox("The field '"+app.g.dd_fieldlabel(arrField[0], arrField[1])+"' is a mandatory field and must be completed",16);
						return false;
					}
				}
			}
		}
	}

	//-- call api to create in kbase then save as normal in swdata

	//-- set catalogname - so can be used in sqllists
	//-- F0104998
	//swkb().catalogname = _swdoc.MF().s_kb__catalogid.display;

	return _swdoc.swkb_addarticle()

}

						OnDataSaved()
{
	//-- attach _swdoc if it has changed
	_swdoc.upload_external_document();
	//-- Save Last Update Modified Datex
	_swdoc.swkb().lastmodifieddatex = app.g.todaysdate_epoch();

	var strUpdatedText = ""
	if(!_swdoc.boolInserting)
	{
		for(strDataRef in _swdoc.array_changes)
		{
			var oField = _swdoc.array_changes[strDataRef];
			if(oField.newvalue!="")
			{
				if(strUpdatedText!="")strUpdatedText+="\n";
				strUpdatedText += "The field " + oField.name + " was changed from ["+ oField.value +"] to [" +oField.newvalue+ "]";
			}
		}
		if(strUpdatedText!="")app.oKB.create_articleupdateentry(_swdoc.swkb().docref,strUpdatedText)
		_swdoc.MF().sl_history.Refresh();
	}
	else
	{
		MessageBox("The knowledgebase article " + _swdoc.swkb().docref + " was successfully created");
		if(swkb_articles.callref>0)
		{
			app.oKB.relate_call_to_kb(swkb_articles.docRef,swkb_articles.callref,'Used');		
		}
	}

	_swdoc.boolInserting = false;
	_swdoc.boolChanges = false;  // used to tell if there has been changes when pressing the 'Preview Article' button


	_swdoc.MF().OnFormLoaded(true);

}

							s_kb__callprobcode_OnLeftClick()
{
	//-- required for modeless message box
	var funcPushKeyWords = function(arrKeywords)
	{
		for(var i = 0; i<arrWords.length; i++)
		{
			var thisWord = arrWords[i];
			if(thisWord=="->")
			{
			}else
			{
				if(!app.in_array(arrKeywords,thisWord))
				{
					arrKeywords.push(thisWord);
				}
			}
		}
		_swdoc.swkb().keywords = arrKeywords.join(",");
		//-- only if updating record change
		if(!_swdoc.boolInserting)_swdoc.array_changes["swkb_articles.keywords"].newvalue = _swdoc.swkb().keywords;
	}

	//-- required for modeless message box		
	var funcUpdateReturn = function()
	{
		_swdoc.UpdateFormFromData();
		return true;
	}

	//-- popup picker
	var strOldCode = _swdoc.swkb().callprobcode;
	app.g.pick_problemprofile(_swdoc.swkb().callprobcode, "", false, function(strCode)
	{
		if (strCode===false)
		{
			return;
		}	
		_swdoc.swkb().callprobcode = strCode;
		if(strCode=="")
			app._ete(mainform.s_kb__callprobcode,"");
		_swdoc.UpdateFormFromData();
		//-- STORE CHANGE
		if(!_swdoc.boolInserting)_swdoc.array_changes["swkb_articles.callprobcode"].newvalue = strCode;	

		//-- set desc value so can be used in preview and sqllists
		_swdoc.swkb().profiledesc = s_kb__callprobcode.display;

		var strProfileCode = strCode;

		if(strProfileCode!=strOldCode)
		{
			var recPCInfo = app.g.get_record("pcdesc", strCode);
			if(recPCInfo!=false)
			{
				arrWords = recPCInfo.info.split(" ");
				var strKeywords = _swdoc.swkb().keywords;
				if(strKeywords=="")
				{
					var arrKeywords = new Array();
					funcPushKeyWords(arrKeywords);
					funcUpdateReturn();
				}
				else
				{
					MessageBox("You already have keywords defined for this KnowledgeBase article.\n\nTo merge the new keywords into the existing ones, click Yes\nTo overwrite the existing keywords, click No\nTo keep the existing keywords and ignore the new ones, click Cancel",35,function(intReturn)
					{
						if(intReturn==2)
						{
							return false;
						}
						if(intReturn==6)
						{
							var arrKeywords = strKeywords.split(",");
						}
						else
						{
							var arrKeywords = new Array();
						}
						funcPushKeyWords(arrKeywords);
						funcUpdateReturn();
					});
				}
			}
			else
			{
				funcUpdateReturn
			}
		}
	});	
}

							s_kb__callprobcode_OnRightClick()
{
	_swdoc.swkb().callprobcode = "";
	app._ete(s_kb__callprobcode,"");
	_swdoc.UpdateFormFromData();

	return true;

}

							OnFormLoaded(bDataReloaded)
{
	// DTH: F0097669 KB Cat Rights	
	_swdoc.arrKbCatRights = app.g.get_anlyst_kbcat_rights(app.session.analystId, _swdoc.swkb().catalog);
	_swdoc.arrKbAnalystCats = app.g.get_authorised_catalog_names(app.session.analystId);

	if(_swdoc.swkb().docref=="")
	{
		// START F106395
		if (_swdoc.arrKbAnalystCats[0]!=null)
		{
			// Set 'catalog' to the first one that the current analyst has rights for
			_swdoc.swkb().catalog = _swdoc.arrKbAnalystCats[0];
		}
		// END F106395

		//-- check if we have params to populate form with
		app.g.popuplate_form_fromargs("swkb_articles", _swdoc, false, false);

		//swkb().callprobcode.text = _swdoc.GetArg("swkb_articles.callprobcode");

		//-- inserting
		_swdoc.boolInserting = true;

		//-- set default values
		//--F0086382
		var intCurrentTime =app.global.GetCurrentEpocTime(); //-- date
		_swdoc.swkb().docdatex = ""+intCurrentTime;

		_swdoc.swkb().owneranalystid = app.session.analystId;     //-- owner
		_swdoc.swkb().ownergroupid = app.session.groupId;

		_swdoc.swkb().docflags = "1"; //- -visible to customers


		//-- set default process settings		
		var oRec = app.g.get_record("swkb_catalogs", _swdoc.swkb().catalog);
		if(oRec)
		{
			_swdoc.swkb().fk_bpm_process = oRec.fk_bpm_process;
			_swdoc.swkb().fk_bpm_assignid = oRec.fk_bpm_assignid;
			_swdoc.swkb().fk_bpm_assigngrp = oRec.fk_bpm_assigngrp;
			_swdoc.swkb().fk_bpm_priority = oRec.fk_bpm_priority;
			_swdoc.swkb().fk_bpm_sla = oRec.fk_bpm_sla;
			_swdoc.swkb().catalogname = oRec.catalogname;
			_swdoc.UpdateFormFromData();	
		}
		//-- set default author
		swkb_articles.author = s_kb__owner_name.display;

		var strItems = app.g.get_anlyst_kbcats_foradd(app.session.analystId);
		if(strItems=="")
		{
			MessageBox('You do not have permission to create documents for any catalog.');
			_swdoc.CloseForm();
		}
		_swdoc.MF().s_kb__catalogid.pickList = strItems;

		//-- if title is not blank means we have come from resolve form - so do not reset flag
		if(_swdoc.swkb().title=="")_swdoc.ResetModiedFlag("swkb_articles");


	}
	else
	{
		_swdoc.SetTitle("Manage KnowledgeBase Article");
		_swdoc.boolInserting = false;

		for(var x=0; x< mainform.elements.length;x++)
		{
			var oE = mainform.elements[x];
			if(oE.dataRef!=""  && ((oE.type == FE_FIELD)||(oE.type == app.FE_SEARCHFIELD)))
			{

				var arrI = oE.dataRef.split(".");
				if(dd.tables[arrI[0]].columns[arrI[1]])
					var strFormattedValue = dd.tables[arrI[0]].columns[arrI[1]].FormatValue(oE.text);

				_swdoc.array_changes[oE.dataRef] = new Object();
				//_swdoc.array_changes[oE.dataRef].name = dd.tables[arrI[0]].columns[arrI[1]].DisplayName;
				app._eva(_swdoc.array_changes[oE.dataRef] , strFormattedValue);
				_swdoc.array_changes[oE.dataRef].newvalue = "";
			}
		}

		if (!app.in_array(_swdoc.arrKbAnalystCats, _swdoc.swkb().catalogname)) // Analyst does not have this catalog in his / her list based on rights
		{
			if(_swdoc.arrKbAnalystCats[_swdoc.swkb().catalog]==undefined)
			{
				app._een(_swdoc.MF().s_kb__catalogid, false);
				app._evi(_swdoc.MF().s_kb__catalogid, false);
				app._evi(_swdoc.MF().eb_catalog_name, true);
			}
			else
			{
				if(_swdoc.arrKbCatRights["editcatalog"] ==1)
				{
					_swdoc.swkb().catalogname = _swdoc.arrKbAnalystCats[_swdoc.swkb().catalog];
					var strItems = app.g.get_anlyst_kbcats_foradd(app.session.analystId);
					if(strItems=="")
					{
						MessageBox('You do not have permission to create documents for any catalog.');
						_swdoc.CloseForm();
					}
					_swdoc.MF().s_kb__catalogid.pickList = strItems;
					_swdoc.ResetModiedFlag("swkb_articles");
				}
				else
				{
					app._een(_swdoc.MF().s_kb__catalogid, false);
					app._evi(_swdoc.MF().s_kb__catalogid, false);
					app._evi(_swdoc.MF().eb_catalog_name, true);
				}
					//MF().s_kb__catalogid.pickList = _swdoc.arrKbAnalystCats.join("|");
			}
		}
		else
		{
			//-- load catalog list - before data is loaded
			// _swdoc.get_catalog_list();
			// above line commented out as part of 106478.

			// START F106478
			var strItems = app.g.get_anlyst_kbcats_foradd(app.session.analystId);
			_swdoc.MF().s_kb__catalogid.pickList = strItems;
			// END F106478
		}

	}

	if(swkb_articles.callref!="")
		app._een(btn_call, true);

	var boolSetting = app.oSettings.get_setting("boolKnowledgePreviewEnable");
	if(!boolSetting)
	{
		app._evi(_swdoc.MF().btnPreview, false);
		app._evi(_swdoc.MF().Button1, false);
		app._evi(_swdoc.MF().btnPreview1, false);
		app._evi(_swdoc.MF().Button2, false);
	}

	TabInfo.ShowTabItem(2,!_swdoc.boolInserting);
	TabInfo.ShowTabItem(3,!_swdoc.boolInserting);
	TabInfo.ShowTabItem(4,!_swdoc.boolInserting);
	if(!bDataReloaded)app._etab(TabInfo,0);

	_swdoc.setup_based_on_rights();

}

							s_kb__ownergroupname_OnLeftClick()
{
	//-- pick profile code
	app.g.pick_analystorgroup("Select article owner", function(oI)
	{
		if(oI)
		{
		_swdoc.swkb().owneranalystid = oI.oAnalyst.id;
		_swdoc.swkb().ownergroupid = oI.oGroup.id;
		_swdoc.swkb().author = oI.oAnalyst.name;
		_swdoc.UpdateFormFromData();

		//-- STORE CHANGES
		if (!_swdoc.boolInserting) {
							_swdoc.array_changes["swkb_articles.owneranalystid"].newvalue = oI.oAnalyst.id;	
							_swdoc.array_changes["swkb_articles.ownergroupid"].newvalue = oI.oGroup.id;
							}

		}
	});

	return true;
}

