							_swdoc.array_changes["swkb_articles.ownergroupid"].newvalue = oI.oGroup.id;
							}


		_swdoc.UpdateFormFromData();

		return true;

}

							mbtn_ici_views_OnMenuItem(strName, nItemID)
{

	var iProperties = 1;
	var iVCMchildren = 2;	
	var iVCMparents = 5;		
	var iInventory = 3;
	var iChangeSchedule = 4;

	//-- get intCI
  	var intCI = app.g.sl_getrawcolvalue(sl_configitems,-1,"pk_auto_id");
  	if(intCI>0)
  	{
  		//-- pass in text identfier as well
  		var strItem = app.g.sl_getrawcolvalue(sl_configitems,-1,"ck_config_item");

		if(nItemID == iProperties)
		{
			app.cmdb.popup_configitem(intCI,true,"",false,function()
			{
				return;
			});
		}
		else if(nItemID == iVCMchildren)
		{
			app.cmdb.view_vcm(intCI,false);
		}
		else if(nItemID == iVCMparents)
		{
			app.cmdb.view_vcm(intCI,true);
		}

		else if(nItemID == iInventory)
		{
			app.cmdb.view_inventory(intCI,strItem);
		}
		else if(nItemID == iChangeSchedule)
		{
			app.cmdb.view_schedule(intCI,strItem);
		}
	}
}

							mbtn_ici_actions_OnMenuItem(strName, nItemID)
{
	var iRemoveCI = 1;

	//-- Remove Selected config items
	if(nItemID == iRemoveCI)
	{

		//-- do this by finding unselected rows and making sure they are part of fitler
		var strFilter = "";
		for (var x=_swdoc.MF().sl_configitems.rowCount()-1;x > -1;x--)
		{
			var value = _swdoc.MF().sl_configitems.GetItemTextRaw(x,"pk_auto_id");		
			if (!_swdoc.MF().sl_configitems.IsRowSelected(x))
			{
				//-- add to filter
				if (value != "")
				{
    				if (strFilter != "") strFilter +=",";
    				strFilter += value;
				}
			}
			else
			{
				app.oKB.unrelate_ci_from_kb(_swdoc.swkb().docref,value);
			}
		}
		if(strFilter=="")
			strFilter = "0";

		app._slf(_swdoc.MF().sl_configitems,"pids="+strFilter);
        _swdoc.MF().sl_configitems.Refresh();
        _swdoc.MF().sl_configitems_OnItemSelected(0);

       return;
	}

}

							btn_search_ci_OnPressed()
{
    //-- NWJ 08.03.2004
    //-- open CI search form in select mode
	var strURL = "";//canreset=1&fk_company_id=" + pfu(oCompany().pk_company_id);
	app.cmdb.search_ci(true,strURL,function(sResults)
	{
		if (sResults.selectedkeys=="") return;

		var strFilter = app.g.sl_get_valuesdel(_swdoc.MF().sl_configitems,"pk_auto_id",",","",false);
  		var arrKeys = sResults.selectedkeys.split(",");
  		for(var x in arrKeys)
  		{
			app.oKB.relate_ci_to_kb(arrKeys[x],_swdoc.swkb().docref);

			if (strFilter != "") 
			{
				strFilter += "," + arrKeys[x];
			}
			else
			{
				strFilter = arrKeys[x];
			}
		}	

		app._slf(_swdoc.MF().sl_configitems, "pids="+strFilter);
		_swdoc.MF().sl_configitems.Refresh();
	});	
}

