							mbtn_ctype_remove_OnPressed()
{



		//-- do this by finding unselected rows and making sure they are part of fitler
		for (var x=_swdoc.MF().sl_kb_type.rowCount()-1;x > -1;x--)
		{
			var value = _swdoc.MF().sl_kb_type.GetItemTextRaw(x,"fk_config_type");		
			if (_swdoc.MF().sl_kb_type.IsRowSelected(x))
			{
				app.oKB.unrelate_ci_type_from_kb(_swdoc.swkb().docref,value);
			}
		}

       _swdoc.MF().sl_kb_type.Refresh();
       sl_kb_type_OnItemSelected(0);

}

							btn_search_ctype_OnPressed()
{
	var strURL = "";
	app.cmdb.select_configtype("Please select the type of item to associate",true,function(strType)
	{
		if (strType=="")
		{
			return;
		}

		var oCIType = app.g.get_record("config_typei", strType);
		app.oKB.relate_ci_type_to_kb(oCIType.pk_config_type,oCIType.type_display, _swdoc.swkb().docref);
		_swdoc.MF().sl_kb_type.Refresh();
		sl_kb_type_OnItemSelected(0);
		var intReturn = confirm("Would you like to associate the KB Documents with the existing CI(s) of this type?");
		if(intReturn)
		{
			var xmlmc = new XmlMethodCall;
			xmlmc.SetValue("docref", _swdoc.swkb().docref);
			xmlmc.SetValue("configType", oCIType.pk_config_type);

			//-- Invoke the method
			if(xmlmc.Invoke("VPME", "relateKBtoCIs"))
			{

			}
			else
			{
				MessageBox(xmlmc.GetLastError());
			}	
		}

  		//-- prompt to add name to keywords
		var intReturn = confirm("Would you like to add this configuration type to the article keywords so it can be used in knowledge searches?");
		if(!intReturn)return;

		var strKeywords = _swdoc.swkb().keywords;
		var arrKeywords = strKeywords.split(",");

		var arrTypes = oCIType.pk_config_type.split("->");
		for(var x=0;x<arrTypes.length;x++)
		{
			if(!app.in_array(arrKeywords,arrTypes[x]))
			{
				arrKeywords.push(arrTypes[x]);
			}
		}
		_swdoc.swkb().keywords = arrKeywords.join(",");
		_swdoc.array_changes["swkb_articles.keywords"].newvalue = _swdoc.swkb().keywords;	
		_swdoc.UpdateFormFromData();
	}); 
}

