							TabOther_OnTabSelected(nTab)
{
	var tRFC = 0;
	var tCI = 1;
	var tCtype = 2;
	var tHistory = 3;
	var tSubscribers = 4;
	var tRequests = 5;
	if(nTab==7)
	{
		if(_swdoc.swkb().flg_legislation =="Yes")
			app._een(_swdoc.MF().Field7, true);
	}
	if(nTab==tRFC)
	{
		var strCalls = app.oKB.get_kb_calls(_swdoc.swkb().docref,"RFC-KB");
		app._slf(_swdoc.MF().sl_rfcs,"crs=" + strCalls + "&sf=open_requests");
		_swdoc.MF().sl_rfcs.Refresh();
	}
	else if(nTab==tRequests)
	{
		var strCalls = "";

		var ap = {};
		ap.fk_kbdoc = _swdoc.swkb().docref;
		var oRS = app.g.get_tablerecordset_bycol("cmn_rel_opencall_kb",ap,false,"fk_callref"); 

		while(oRS.Fetch())
		{
			var currKey = app.g.get_field(oRS,"fk_callref");
			if (strCalls!="")strCalls +=",";
			strCalls += currKey;
		}
		if(strCalls=="")strCalls="-1";

		app._slf(_swdoc.MF().sl_requests1,"crs=" + strCalls + "&sf=open_requests");
		_swdoc.MF().sl_requests1.Refresh();
	}
	else if(nTab==tCI)
	{
		//-- get ci's associated to this article
		var strCIs = app.oKB.get_kb_cis(_swdoc.swkb().docref);
		app._slf(_swdoc.MF().sl_configitems,"pids="+strCIs);
		sl_configitems_OnItemSelected(0);
		_swdoc.MF().sl_configitems.Refresh();
	}
	else if(nTab==tSubscribers)
	{
		var nCount = app.g.sqs_rowcount("count/analyst_subscription_to_doc", "doc="+app.pfu(_swdoc.MF().s_kb__docref.value));
		if(nCount>0)
		{
		    app._een(_swdoc.MF().but_sub, false);
		    app._een(_swdoc.MF().but_unsub, true);
		}
		else
		{
		    app._een(_swdoc.MF().but_sub, true);
		    app._een(_swdoc.MF().but_unsub, false);
		}
		_swdoc.MF().sl_subs.Refresh();
	}
	else if(nTab==tCtype)
	{
		app._slf(_swdoc.MF().sl_kb_type,"swc=kb_docref&kvs="+app.pfu(_swdoc.swkb().docref));
		sl_kb_type_OnItemSelected(0);
		_swdoc.MF().sl_kb_type.Refresh();
	}
	else if (nTab==tHistory)
	{
		_swdoc.MF().sl_history.Refresh();
	}


}

