							btnPreview_OnPressed()
{
	var funcShowArticle = function()
	{
		if (boolShowArticle)
		{
			app.global.ShellExecute(app.g.get_webserver()+"/sw/clisupp/details/swkbase.preview.php?docref="+_swdoc.swkb().docref+"&sessid="+app.session.sessionId+"&ColourScheme="+getColourScheme, "", "", "", "");
		}
	}

	if(IsObjectDefined("_bHtmlWebClient"))
    {
    	var getColourScheme = 4; //silver default
    }else
    {
    	var getColourScheme = app.g.get_CurrentSkinColorSchema();
    }

	if(_swdoc.MF().s_kb__problem.text != "")
	{
		var boolShowArticle = false; // used to determine if the article should be previewed

		// check if any changes have been made to the article
		if (_swdoc.boolChanges)

		{
			MessageBox("Before viewing this KB article you must save it. Would you like to save this KB article?", MB_OKCANCEL | MB_ICONQUESTION , function(intSaveArticle)
			{
				if(intSaveArticle==1)
				// if user wants to save article...
				{
					_swdoc.Save(); //..save article

					if(_swdoc.boolChanges==false)
					// if _swdoc saved correctly...
					{
						boolShowArticle = true; // ...show article
					}
				}
				funcShowArticle();
			});
		}
		else
		{
			boolShowArticle = true;
			funcShowArticle();
		}
	}
	else
	{
		app.global.MessageBox("There is no article content to preview", MB_ICONEXCLAMATION | MB_OK);
	}

}

