function setup_company_form()
{
	var bRemoteSupportEnabled = false;
	bRemoteSupportEnabled = app.g.remote_support_enabled();

	//-- show hide any tab items fields based on mode
    _swdoc.MF().tabCompany.ShowTabItem(1,!_swdoc.boolInserting);
	_swdoc.MF().tabCompany.ShowTabItem(2,!_swdoc.boolInserting);
    _swdoc.MF().tabCompany.ShowTabItem(3,!_swdoc.boolInserting);
	_swdoc.MF().tabCompany.ShowTabItem(4,!_swdoc.boolInserting);        
	_swdoc.MF().tabCompany.ShowTabItem(5,!_swdoc.boolInserting);        	
	_swdoc.MF().tabCompany.ShowTabItem(6,!_swdoc.boolInserting);        	
	_swdoc.MF().tabCompany.ShowTabItem(7,!_swdoc.boolInserting);        	
	_swdoc.MF().tabCompany.ShowTabItem(8,!_swdoc.boolInserting);
	_swdoc.MF().TabContact.ShowTabItem(2,(!_swdoc.boolInserting && (bRemoteSupportEnabled)));
	app._etab(_swdoc.MF().TabContact,0);
	app._evi(_swdoc.MF().mb_cc, !_swdoc.boolInserting);
	app._evi(_swdoc.MF().mb_ccview, !_swdoc.boolInserting);
	app._evi(_swdoc.MF().mb_view, !_swdoc.boolInserting);

	if(!_swdoc.boolInserting)
	{
		//-- only show contracts is ext companies is on
		_swdoc.MF().tabCompany.ShowTabItem(8,((dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")==1)));

		//_swdoc.load_company_contacts();	
		//_swdoc.load_company_sites();	
		//_swdoc.load_company_contracts();	
		//_swdoc.load_company_cis();	
		_swdoc.load_company_services();	
	}
	if(_swdoc.GetArg("fk_company_id")!=""){
		_swdoc.oCompany().fk_company_id = unescape(_swdoc.GetArg("fk_company_id"));
		app._ete(_swdoc.MF().lb_company,  unescape(_swdoc.GetArg("fk_company_id")));
		app._eva(_swdoc.MF().lb_company,  unescape(_swdoc.GetArg("fk_company_id")));
	} 

	app._eva(_swdoc.MF().flg_direct_children_only, 1);
	app._slf(_swdoc.MF().sl_orgs, "swc=fk_company_id&kvs="+app.pfu(_swdoc.oCompany().pk_company_id)+"&sf=has_company");
	_swdoc.MF().sl_orgs.Refresh();
	app._etab(_swdoc.MF().tabCompany, 0);

		//--124775: Only show Subscription actions button if you have writes and you are not inserting
	app._evi(_swdoc.MF().mb_serv_action, (app.service.can_manage_cost_and_subs(false) && !_swdoc.boolInserting));

}

function load_company_cis()
{
	_swdoc.companyCIIDS = app.cfg.get_company_cikeys(_swdoc.oCompany().pk_company_id,true,true);	
	app._slf(_swdoc.MF().sl_ci, "pids=" + app.pfu(_swdoc.companyCIIDS) + "&sf=cilist_activebln,cilist_exludemes");
	_swdoc.MF().sl_ci.SetRowSelected(0);				

	_swdoc.load_company_services();
}

function load_company_services()
{
	//Load services from the app.service catalog
	if(_swdoc.oCompany().pk_company_id!="")
	{
		var serviceKeys = app.service.get_organisation_services(_swdoc.oCompany().pk_company_id,true,true);

		if(serviceKeys!="")
		{

			//SG F0112199 - Filter out archived services from list
			var nonArchivedServices = "-1";
			var oRS = app.g.get_sqrecordset("service/process/get_non_archived_services","cids="+serviceKeys);
			while(oRS.Fetch())
			{
				strRelKey = app.g.get_field(oRS,"PK_AUTO_ID");
				nonArchivedServices +=",";
				nonArchivedServices += strRelKey;
			}
			//SG End archived filter
			app._slf(_swdoc.MF().sl_services, "pids="+app.pfu(nonArchivedServices));
		}
		else
			app._slf(_swdoc.MF().sl_services, "pids=0");
	}
}

function load_company_contacts()
{
	app._slf(_swdoc.MF().sl_contact , "swc=fk_company_id&kvs=" + encodeURIComponent(_swdoc.oCompany().pk_company_id));
	_swdoc.MF().sl_contact.Refresh();
	_swdoc.MF().sl_contact.SetRowSelected(0);				
}

//ES - F0110887 - function to retrieve setting value and convert to epoch
function warningDaysEpochValue()
{
	var settingValue = app.oSettings.get_setting("strOrgSupportedbyWarningDays");
	if (settingValue<=0)
	{
		return settingValue;
	}
	else
	{			
		var settingValueEpoch = settingValue*86400;
		return settingValueEpoch;
	}
}

//ES - F0110887 - function to set colour based on Org's Supported Until date
function setSupportedUntilColour()
{
	var supportedbyxTime = app.g.fix_epoch(_swdoc.oCompany().supportexpiryx);
	if (app.global.GetCurrentEpocTime()<supportedbyxTime)
	{
		var periodValue = supportedbyxTime-app.global.GetCurrentEpocTime();
		if ((_swdoc.warningDaysEpochValue()!=0 || _swdoc.warningDaysEpochValue()>0) && periodValue<=_swdoc.warningDaysEpochValue())
		{
			app._ecol(_swdoc.MF().supportexpiryx_datefield , '#ff8000');
		}
		else
		{
			app._ecol(_swdoc.MF().supportexpiryx_datefield , '#24c000');
		}
	}
	else if (app.global.GetCurrentEpocTime()>=supportedbyxTime)
	{
		app._ecol(_swdoc.MF().supportexpiryx_datefield , '#FF0000');
	}
}

						OnDataSaved()
{
	//RJC 05.01.09
	// If there is a parent company id, refresh parent doc, and create child relationship
   	if(_swdoc.oCompany().fk_company_id!=""){
       	var pDoc = app.g.get_me_document("COMPANY",_swdoc.oCompany().fk_company_id);
    	if(pDoc)
    	{
    		var intCurSel = pDoc.MF().sl_orgs.curSel;
    		pDoc.MF().sl_orgs.Refresh();
    		pDoc.MF().sl_orgs.SetRowSelected(intCurSel,true);
    	}

  		//-- create app.cmdb relationship
    	if(pDoc)
    	{
    		//-- need to reload site record to get fk_cmdb_id
    		_swdoc.LoadRecordData("swdata", "Company", _swdoc.oCompany().pk_company_id);

    		app.cmdb.delete_child_meconfigrelation(_swdoc.oCompany().fk_cmdb_id, "ME->COMPANY");
			//-- check if have default rel if so form silently ..
    		var strRel = "child organisation of";//dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultOrgSiteRelationship");
   			app.cmdb.generate_meconfigrelation(pDoc.oCompany().fk_cmdb_id,_swdoc.oCompany().fk_cmdb_id,strRel);  
    	}else{
			var strParams = "orgs="+app.pfu(_swdoc.oCompany().fk_company_id);
			var oRS = app.g.get_sqrecordset("company/get_company_cikeys",strParams);
			if(oRS.Fetch())
			{
				var fetchKey  = app.g.get_field(oRS,"fk_cmdb_id");
    			var strRel = "child organisation of";//dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultOrgSiteRelationship");
     			app.cmdb.delete_child_meconfigrelation(_swdoc.oCompany().fk_cmdb_id, "ME->COMPANY");
  				app.cmdb.generate_meconfigrelation(fetchKey,_swdoc.oCompany().fk_cmdb_id,strRel);  
			}

    	}

	}

	//if inserting re store the me form, so that new sites can get app.cmdb value
	if(_swdoc.boolInserting)
	{
    	_swdoc.LoadRecordData("swdata", "Company", _swdoc.oCompany().pk_company_id);
    	app.g.add_me_document("COMPANY", _swdoc.oCompany().pk_company_id, _swdoc);
	}

	//-- we were inserting so now re-init form fields for edit
 	_swdoc.boolInserting=false;
	_swdoc.setup_company_form();	
}

