							sl_ci_OnItemSelected(nRow)
{
    //-- Load CI info based on CI loaded
    //-- testing Gerrys new dataloader
    var ci_id  = sl_ci.GetItemTextRaw(nRow,"pk_auto_id");
    if (ci_id == "")
    {
        _swdoc.config_key     = 0;
        _swdoc.config_item    = "";       
        app._een(mbtn_view_ci, false);
    }
    else
    {
    	var ci_item = sl_ci.GetItemTextRaw(nRow,"ck_config_item");
        _swdoc.config_key     = ci_id;
        _swdoc.config_item    = ci_item;
		app._een(mbtn_view_ci, true);        
    }

}

