}

							tb_accman_OnLeftClick()
{
	//-- 09.03.2005
	//-- NWJ
	//-- assign analyst as account manager
	app.g.pick_analyst("Account Manager", function(oA)
	{
		if (oA)
		{
			if(oA.supportgroup=="_THIRDPARTY")
			{
				MessageBox("Third Party cannot be account manager.");
				return false;
			}

			app._ete(tb_accman, oA.name);
			_swdoc.oCompany().fk_accountmanager_id = oA.id;
			_swdoc.UpdateFormFromData();
		}	
	});

	return false;
}

