							mbtn_company_actions_OnMenuItem(strName, nItemID)
{
	var iAddSite = 1;
	var iRemoveSite = 2;
	var iSearchSite = 3;

	//-- Add Site
	if(nItemID == iAddSite)
	{
		var strURL = "fk_company_id=" + app.pfu(_swdoc.oCompany().pk_company_id);
		app.OpenFormForAdd("TfSite", "", strURL, true);
	}
	else if(nItemID == iRemoveSite)
	{
		//-- 13.04.2005
		//-- NWJ
		//-- delete selected site (if it has contacts update them)
    	var selectedRow     = sl_sites.curSel;
    	var strConfirm		= "Are you sure you want to delete this site from the database ?";
		var strSiteKey      = sl_sites.GetItemTextRaw(selectedRow,"site");    

		if (app.g.sl_deldbrow(sl_sites,sl_sites.curSel,'site_name',null,strConfirm))
		{
			//-- remove link to company contacts
			//-- done in vpme
		}
	}
	else if(nItemID == iSearchSite)
	{
		app.g.search_for("site",true,"",function(oRes)
		{
			if(oRes.selectedkeys=="")return;

			var arrKeys = oRes.selectedkeys.split(",");

			//-- get list of sites to associate
			var strMassSites = "";
			for(var x=0; x< arrKeys.length;x++)
			{
				//-- update each site record to store company id
				if(strMassSites != "")strMassSites +=",";
				strMassSites += app.pfu(arrKeys[x]);
			}

			//-- if we have ids - do mass update
			if(strMassSites!="")
			{
				//--
				//-- create company me ci to userdb me ci relation
				if(_swdoc.oCompany().fk_cmdb_id>0)
				{
					//-- get userdb ME CI ids from app.cmdb
					var strSiteCIIDS ="";
					var strParams = "sites="+strMassSites;
					var oRS = app.g.get_sqrecordset("company/get_site_cikeys",strParams);
					while(oRS.Fetch())
					{
						if(strSiteCIIDS!="")strSiteCIIDS+=",";
						strSiteCIIDS += app.g.get_field(oRS,"FK_CMDB_ID");
					}
					if(strSiteCIIDS!="")	
					{
						//-- dis-associate from other companies
						app.cmdb.delete_child_meconfigrelation(strSiteCIIDS, "COMPANY");

						var strRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultOrgSiteRelationship");
						if(app.cmdb.generate_meconfigrelation(_swdoc.oCompany().fk_cmdb_id, strSiteCIIDS,strRel))
						{
						}
					}
				}	

				var strParams = "parentid="+app.pfu(_swdoc.oCompany().pk_company_id)+"&parentname="+app.pfu(_swdoc.oCompany().companyname)+"&sites="+strMassSites;
				app.g.submitsqs("company/set_site_association", strParams);

				sl_sites.Refresh();			
			}
		});
	}
}

