							mbtn_company_contact_actions_OnMenuItem(strName, nItemID)
{
    var iAddContact = 1;
	var iDeleteContact = 2;
	var iRemoteSupport = 3;

	//-- Add Site
	if(nItemID == iAddContact)
	{
		//-- 29.07.2004
    	//-- NWJ
    	//-- Open new contact form
    	//-- pass in pk_company_auto_id so that we can link the two
    	var strURL = "fk_company_id=" + app.pfu(_swdoc.oCompany().pk_company_id) + "&companyname=" + app.pfu(_swdoc.oCompany().companyname);
    	app.OpenFormForAdd("TfCustomer" ,"", strURL, true, function(){
    		_swdoc.load_company_contacts();	
    	})  	
    	//sl_contact.Refresh();
	}
	else if(nItemID == iDeleteContact)
	{
		//-- 29.07.2004
    	//-- NWJ
    	//-- Delete the selected contact
    	var selectedRow     = sl_contact.curSel;
    	var strConfirm		= "Are you sure you want to delete this contact from the database ?";

		if(app.g.sl_deldbrow(sl_contact,selectedRow,"keysearch",null,strConfirm))
		{
			//-- relations deleted in vpme
			_swdoc.load_company_contacts();			
		}
	}
	else if (nItemID == iRemoteSupport)
	{


		//-- Remote Support Enabled?
		if(app.g.remote_support_enabled())
		{
			//-- Attempt to launch a pinned support session
			var strEmail = app.g.sl_getrawcolvalue(sl_contact,-1,"email");
			app.g.start_remote_support(strEmail);
		}	
		else
		{
			MessageBox("Remote Support is not available for this customer");
		}
	}
}

							mbtn_company_ci_actions_OnMenuItem(strName, nItemID)
{
	var iAssignCI = 1;
	var iRemoveCI = 2;	
	var iRemoteSupport = 3;

	if(nItemID == iAssignCI)
	{
		//-- relate the CI to the Company
		app.cmdb.search_ci(true,"",function(sResults)
		{
			if (sResults.selectedkeys=="") return;

			//--
			//-- create ci relationship (always assume that customer always uses a CI)
			if(_swdoc.oCompany().fk_cmdb_id>0)
			{
				var strRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultOrgCIRelationship");
				app.cmdb.generate_meconfigrelation(_swdoc.oCompany().fk_cmdb_id, sResults.selectedkeys, strRel);
			}
			//- ask user to confirm that company is the owner of the CI's	
   			if(confirm("Is this company the owner of the selected configuration item(s)?"))
   			{
				//-- update the config items
				var strParams = "parentid="+app.pfu(_swdoc.oCompany().pk_company_id)+"&parentname="+app.pfu(_swdoc.oCompany().companyname)+"&cikeys="+app.pfu(sResults.selectedkeys)
				app.g.submitsqs("company/set_ci_association",strParams);
			}	

			_swdoc.MF().sl_ci.Refresh();
			return;

		});

    }
	else if(nItemID == iRemoveCI)
	{
		//-- remove link between ci and company
		var intCI = sl_ci.GetItemTextRaw(sl_ci.curSel, 'pk_auto_id');
		if (intCI > 0)
    	{   	
    		if (!confirm("Are you sure you want to unlink the selected configuration item from this company ?")) return;

    		//--
			//-- delete ci relationship (always assume that ME is parent)
			if(_swdoc.oCompany().fk_cmdb_id>0)
			{
				app.cmdb.delete_meconfigrelation(_swdoc.oCompany().fk_cmdb_id, intCI);
			}   		

    		//-- reset owner of ci if company is actual owner
 			var strParams = "parent="+app.pfu(_swdoc.oCompany().pk_company_id)+"&cikey="+app.pfu(intCI)
			if(app.g.submitsqs("company/remove_ci_association",strParams))
        	{
        		_swdoc.MF().sl_ci.Refresh();
        	}
    	}
	}
	else if(nItemID==iRemoteSupport)
	{
		//-- Remote Support Enabled?
		if(app.g.remote_support_enabled())
		{
			//-- Attempt to launch a pinned support session
			var intItem = app.g.sl_getrawcolvalue(sl_ci,-1,"pk_auto_id");
			var oCI = app.g.get_record("config_itemi",intItem);
			app.g.start_pinned_remote_support(oCI);
		}	
		else
		{
			MessageBox("Remote Support is not available for this item");
		}
	}





}

