							mbtn_company_contract_actions_OnMenuItem(strName, nItemID)
{
    var iAddContract = 1;
	var iDeleteContract = 2;

	//-- Add Contract
	if(nItemID == iAddContract)
	{
		var strURL = "mode=select";
		app.OpenForm("browse_slas", strURL, true, function(x)
		{
		var strKeys = x.document.strSelectedKey;
		if(strKeys=="")return;
		var arrKeys = strKeys.split(",");
		for (var x=0; x< arrKeys.length;x++)
		{
			var fk_key = arrKeys[x];
			if(app.g.sl_findrow_byvalue(sl_slad1,fk_key,"pk_slad_id")==-1)
			{
            	var strRel = "Uses";
            	var recSLA = app.g.get_record("itsmsp_slad", fk_key);                               
				app.cmdb.generate_meconfigrelation(_swdoc.oCompany().fk_cmdb_id,recSLA.fk_cmdb_id,strRel);  
			}

		}
		var strSLAs = app.g.get_orgs_slas(_swdoc.oCompany().pk_company_id);
		app._slf(_swdoc.MF().sl_slad1, "swc=pk_slad_id&kvs="+app.pfu(strSLAs));

		sl_slad1.Refresh();
		sl_slad1.SetRowSelected(0);
		sl_slad1_OnItemSelected(0);
		});
	}
	else if(nItemID == iDeleteContract)
	{
		//-- 15.12.2004
		//-- NWJ
		//-- delete contract
		var strSLAID = app.g.sl_getrawcolvalue(sl_slad1,-1,"pk_slad_id");
		if (strSLAID!="")
		{

			if (!confirm("Are you sure you want to dis-associate the selected SLA?")) return;

			var strSLACMDBID = app.g.sl_getcolvalue(sl_slad1, -1, "fk_cmdb_id");
				//recSite = app.g.get_record("SITE", _swdoc.strCurrentSiteName);
			app.cmdb.delete_meconfigrelation(_swdoc.oCompany().fk_cmdb_id,strSLACMDBID);
			var strSLAs = app.g.get_orgs_slas(_swdoc.oCompany().pk_company_id);
			app._slf(_swdoc.MF().sl_slad1, "swc=pk_slad_id&kvs="+app.pfu(strSLAs));

			sl_slad1.Refresh();
			sl_slad1.SetRowSelected(0);
			sl_slad1_OnItemSelected(0);

		}
	}


}

							lb_company_OnDropDown()
{
	//-- DJH - 06.08.07
	//-- load company parent browser
	//-- Call new style company search form and resolve company if one is selected

	app.g.search_for("company",true,"",function(oRes)
	{
		if(oRes.selectedkeys=="")return;

		var arrKeys = oRes.selectedkeys.split(",");
		var arrName = oRes.selectedother.split(",");	
		strHash = arrKeys[0];

		if ((strHash == '') || (strHash == 'none')) return;

		if ((strHash == _swdoc.oCompany().pk_company_id))
		{
			MessageBox("A company cannot be associated as a parent of itself.");
			return true;
		}

		_swdoc.oCompany().fk_company_id = strHash;	

		_swdoc.UpdateFormFromData();	
		return false;
	});
}

