							mbtn_orgs_actions_OnMenuItem(strName, nItemID)
{
	var iAddOrg = 1;
	var iRemoveOrg = 2;
	var iSearchOrg = 3;


	//RJC 05.01.09
	//Add a site, remove association (and ci relationship), associate org (and add relationship)
	//-- Add Site
	if(nItemID == iAddOrg)
	{
		var strURL = "fk_company_id=" + app.pfu(_swdoc.oCompany().pk_company_id);
		app.OpenFormForAdd("TfCompany", "", strURL, true)
	}
	else if(nItemID == iRemoveOrg)
	{

	   	var selectedRow     = sl_orgs.curSel;
    	var strConfirm		= "Are you sure you want to disassociate this organisation?";
		var strOrgKey      = sl_orgs.GetItemTextRaw(selectedRow,"pk_company_id");    
		var strOrgCIID      = sl_orgs.GetItemTextRaw(selectedRow,"fk_cmdb_id");    
		if(strOrgKey==""){

		}else{
			if (!confirm(strConfirm)) return false;
			if(_swdoc.oCompany().fk_cmdb_id>0 && strOrgCIID>0)
			{
				app.cmdb.delete_meconfigrelation(_swdoc.oCompany().fk_cmdb_id,strOrgCIID);
			}
			app.g.submitsqs("company/remove_company_association", "org="+app.pfu(strOrgKey));
			app._een(mbtn_orgs_view, false);
			sl_orgs.Refresh();
		}

	}
	else if(nItemID == iSearchOrg)
	{
		app.g.search_for("company",true,"",function(oRes)
		{
			if(oRes.selectedkeys=="")return;

			var arrKeys = oRes.selectedkeys.split(",");

			//-- get list of sites to associate
			var strMassOrgs = "";
			for(var x=0; x< arrKeys.length;x++)
			{
				if(arrKeys[x] == _swdoc.oCompany().pk_company_id)
				{
					MessageBox('An company cannot be associated as a child of itself.');
				}
				else
				{
					//-- update each site record to store company id
					if(strMassOrgs != "")strMassOrgs +=",";
					strMassOrgs += app.pfu(arrKeys[x]);
				}
			}

			//-- if we have ids - do mass update
			if(strMassOrgs!="")
			{
				//--
				//-- create company me ci to userdb me ci relation
				if(_swdoc.oCompany().fk_cmdb_id>0)
				{
					//-- get userdb ME CI ids from app.cmdb
					var strOrgCIIDS ="";
					var strParams = "orgs="+strMassOrgs;
					var oRS = app.g.get_sqrecordset("company/get_company_cikeys",strParams);
					while(oRS.Fetch())
					{
						if(strOrgCIIDS!="")strOrgCIIDS+=",";
						strOrgCIIDS += app.g.get_field(oRS,"FK_CMDB_ID");
					}
					if(strOrgCIIDS!="")	
					{
						//-- dis-associate from other companies
						app.cmdb.delete_child_meconfigrelation(strOrgCIIDS, "ME->COMPANY");

						var strRel ="child organisation of";// dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultOrgSiteRelationship");
						if(app.cmdb.generate_meconfigrelation(_swdoc.oCompany().fk_cmdb_id, strOrgCIIDS,strRel))
						{
						}
					}
				}	

				var strParams = "parent="+app.pfu(_swdoc.oCompany().pk_company_id)+"&orgs="+strMassOrgs;
				app.g.submitsqs("company/set_company_association", strParams);

				sl_orgs.Refresh();			
			}
		});

	}
}

