							mbtn_contract_actions_OnMenuItem(strName, nItemID)
{
	var iAddContract = 1;
	var iDeleteContract = 2;
	var iSearchContract= 3;

	//-- Add Contract
	if(nItemID == iAddContract)
	{
		//-- 15.12.2004
		//-- NWJ
		//-- create a new contract for company
		var strURL = "fk_company_id=" + app.pfu(_swdoc.oCompany().pk_company_id) + "&type=organisation";
  		app.g.sl_openformforadd(sl_contracts,"itsm_contract", strURL, 1,null);
	}
	else if(nItemID == iDeleteContract)
	{
		//-- 15.12.2004
		//-- NWJ
		//-- delete contract
		var strContractID = app.g.sl_getrawcolvalue(sl_contracts,-1,"pk_contract_id");
		if (strContractID!="")
		{
			if (!confirm("Are you sure you want to delete the selected contract ?")) return;

			//-- delete the actual contract
			app.g.sqlexecute_delete("contract",strContractID);
			_swdoc.MF().sl_contracts.Refresh()
			_swdoc.MF().sl_contracts.SetRowSelected(0);

		}
	}
	else if(nItemID == iSearchContract)
	{
		var oRes = app.g.search_for("contract");
		if(oRes.selectedkeys=="")return;

		var arrKeys = oRes.selectedkeys.split(",");

		//-- get list of sites to associate
		var strMassContracts= "";
		for(var x=0; x< arrKeys.length;x++)
		{
			//-- update each site record to store company id
			if(strMassContracts != "")strMassContracts +=",";
			strMassContracts += app.pfu(arrKeys[x]);
		}

		//-- if we have ids - do mass update
		if(strMassContracts!="")
		{
			//--
			//-- create company me ci to userdb me ci relation
			if(_swdoc.oCompany().fk_cmdb_id>0)
			{
				//-- get userdb ME CI ids from app.cmdb
				var strContCIIDS ="";
				var strParams = "contracts="+strMassContracts;
				var oRS = app.g.get_sqrecordset("company/get_contract_cikeys",strParams);
				while(oRS.Fetch())
				{
					if(strContCIIDS!="")strContCIIDS+=",";
					strContCIIDS += app.g.get_field(oRS,"FK_CMDB_ID");
				}
				if(strContCIIDS!="")	
				{
					//-- dis-associate from other companies
					app.cmdb.delete_child_meconfigrelation(strContCIIDS, "COMPANY");

					var strRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultOrgContractRelationship");
					if(app.cmdb.generate_meconfigrelation(_swdoc.oCompany().fk_cmdb_id, strContCIIDS,strRel))
					{

					}
				}
			}	

			var strParams = "parent="+app.pfu(_swdoc.oCompany().pk_company_id)+"&contracts="+strMassContracts;
			app.g.submitsqs("company/set_contract_association", strParams);

			sl_contracts.Refresh();			
		}
	}    


}

