							btn_prev_OnPressed()
{
	_swdoc.intCIPageNo--;

	//-- Add Paging
	var strFilter = "&strFilter="+_swdoc.strCIFilter;
	strFilter += "&paged=true&start="+_swdoc.intCIPageNo+"&end=&cmdb_type='ME->COMPANY'&fk_cmdb_id="+_swdoc.oCompany().fk_cmdb_id+"&webclient="+_swdoc.webclient+"";

	//-- apply filter
	app.g.sl_filter(sl_ci,strFilter,null,0)
	_swdoc.MF().sl_ci.Refresh();

	if (_swdoc.intCIPageNo == 1) 
	{
		app._een(_swdoc.MF().btn_prev,false);
	}
	app._een(_swdoc.MF().btn_next,true);

}

							btn_next_OnPressed()
{
	_swdoc.intCIPageNo++;

	//-- Add Paging
	var strFilter = "&strFilter="+_swdoc.strCIFilter;
	strFilter += "&paged=true&start="+_swdoc.intCIPageNo+"&end=&cmdb_type='ME->Company'&fk_cmdb_id="+_swdoc.oCompany().fk_cmdb_id+"&webclient="+_swdoc.webclient+"";

	//-- apply filter
	app.g.sl_filter(sl_ci,strFilter,null,0)
	_swdoc.MF().sl_ci.Refresh();

	if(sl_ci.rowCount() < _swdoc.intPerPage)
	{
		app._een(_swdoc.MF().btn_next,false);	
	}
	app._een(_swdoc.MF().btn_prev,true);


}

							btn_reset_OnPressed()
{
	app._ete(_swdoc.MF().fd_description, "");
	app._ete(_swdoc.MF().fd_id, "");
	app._ete(_swdoc.MF().fd_type,"");
	app._een(_swdoc.MF().btn_next,false);
	app._een(_swdoc.MF().btn_prev,false);
	_swdoc.strCIFilter = "";
	app._slf(_swdoc.MF().sl_ci , "empty=1");
	_swdoc.MF().sl_ci.Refresh();

}

							btn_search_OnPressed()
{
	_swdoc.strCIFilter = ""; 
	var intPageNo = 1;
	_swdoc.intCIPageNo = 1;
	var strFilter = ""

	if(fd_type.value != ""){
		_swdoc.strCIFilter += " AND fk_child_type LIKE '" + fd_type.value + "%'";
	}
	if(fd_id.value != ""){
		_swdoc.strCIFilter += " AND fk_child_itemtext LIKE '% " + fd_id.text + " %'";
	}
	if(fd_description.value != ""){
		_swdoc.strCIFilter +=" AND childdesc LIKE '% " + app.global.PrepareForSQL(fd_description.value) + " %'";
	}

	//--Update Document so Filter is Global
	strFilter = "&strFilter="+_swdoc.strCIFilter;

	//-- Add Paging
	strFilter += "&paged=true&start="+_swdoc.intCIPageNo+"&end=&cmdb_type='ME->COMPANY'&fk_cmdb_id="+_swdoc.oCompany().fk_cmdb_id+"&webclient="+_swdoc.webclient+"";

	//-- apply filter
	app.g.sl_filter(sl_ci,strFilter,null,0)
	_swdoc.MF().sl_ci.Refresh();

	//Show Next Button
	if(sl_ci.rowCount() < _swdoc.intPerPage)
	{
		app._een(_swdoc.MF().btn_next,false);	
	}else
	{
		app._een(_swdoc.MF().btn_next,true);	
	}
	app._een(_swdoc.MF().btn_prev,false);
}

