							btn_search1_OnPressed()
{
	_swdoc.strCALLFilter = ""; 
	var intPageNo = 1;
	_swdoc.intCALLPageNo = 1;
	var strFilter = ""

	if(fd_class.value != ""){
		_swdoc.strCALLFilter += " AND CALLCLASS = '" + fd_class.value + "'";
	}
	if(fd_id1.value != ""){
		var strCallRef = app.global.GetCallRefValue(_swdoc.MF().fd_id1.text);	
		_swdoc.strCALLFilter += " AND callref LIKE '% " + strCallRef + " %'";
	}

	//--Update Document so Filter is Global
	strFilter = "&strFilter="+_swdoc.strCALLFilter;
	//-- Add Paging
	strFilter += "&paged=true&start="+_swdoc.intCALLPageNo+"&end=&org_id="+encodeURIComponent(_swdoc.oCompany().pk_company_id)+"&webclient="+_swdoc.webclient+"";

	//-- apply filter
	app.g.sl_filter(sl_incs1,strFilter,null,0)
	_swdoc.MF().sl_incs1.Refresh();

	if(_swdoc.MF().sl_incs1.rowCount()  < _swdoc.intPerPage)
	{
		app._een(_swdoc.MF().btn_call_next,false);	
	}else
	{
		app._een(_swdoc.MF().btn_call_next,true);	
	}
	app._een(_swdoc.MF().btn_call_prev,false);
}

