function setup_form_fields(bDataReloaded)
{
	if ( (_swdoc.oCust().keysearch=="") || (_swdoc.oCust().firstname=="")	)
	{
		_swdoc.boolInserting=true;

		app._evi(_swdoc.MF().btn_remotesupport, false);

		//-- clear distinct picklists as client bug where it remembers last value
		app._ete(_swdoc.MF().lb_manager, "");

		//-- set company and/or site
		_swdoc.oCust().fk_company_id = unescape(_swdoc.GetArg("fk_company_id"));		
		_swdoc.oCust().site = unescape(_swdoc.GetArg("site"));
		if((_swdoc.oCust().fk_company_id=="")&&(dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")!=1))
		{
			_swdoc.oCust().fk_company_id = dd.GetGlobalParamAsString("Application Settings/DefaultCompanyID");
		}		

	}
	else
	{
		_swdoc.SetTitle("Customer Properties");

		_swdoc.boolInserting=false;

		//-- Set Remote Support visibility
		if(app.g.remote_support_enabled())
		{
			app._evi(_swdoc.MF().btn_remotesupport, true);
		}
		else
		{
			app._evi(_swdoc.MF().btn_remotesupport, false);
		}

		//-- load customer sites
		_swdoc.strCurrentCo = _swdoc.oCust().fk_company_id;
		_swdoc.strCurrentSiteName = _swdoc.oCust().site;

		_swdoc.refresh_customer_services();

		//-- Enable SLA association
		app._een(_swdoc.MF().sl_defslas, true);
		app._een(_swdoc.MF().btn_add_class, true);
		app._een(_swdoc.MF().btn_rem_class, true);
	}

	if(_swdoc.oCust().fk_company_id=="")
	{
		app._een(_swdoc.MF().other_items, false);
	}else
	{
		app._een(_swdoc.MF().other_items, true);
	}

	app._een(_swdoc.MF().mb_new_call, (!_swdoc.boolInserting));
	_swdoc.MF().Tab1.ShowTabItem(3,(!_swdoc.boolInserting));
	_swdoc.MF().Tab1.ShowTabItem(4,(!_swdoc.boolInserting));
	_swdoc.MF().Tab1.ShowTabItem(5,(!_swdoc.boolInserting));

	//###20120717
	_swdoc.MF().TabServ.ShowTabItem(3,(!_swdoc.boolInserting));

	//-- 12.07.2008 
	//-- disable or hide company field if internal support only
	app._een(_swdoc.MF().lb_company1, ((dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")==1)));
	if(!_swdoc.MF().lb_company1.enable)
	{
		//-- check if we should hide
		app._evi(_swdoc.MF().lb_company1, (dd.GetGlobalParamAsString("Application Settings/HideCompanyField")!=1));		
	}
	app._evi(_swdoc.MF().lbl_company, _swdoc.MF().lb_company1.visible);	
	app._evi(_swdoc.MF().popup_company1, _swdoc.MF().lb_company1);
	//-- eof hide / enable


	if(bDataReloaded)
	{
		//-- do any data loadin when another customer record is loaded from db entity search view
		if(_swdoc.oCust().fk_manager=="")
		{
			app._ete(_swdoc.MF().lb_manager, "");
		}
	}

	_swdoc.strDepartment = _swdoc.oCust().department;
	_swdoc.strSubDepartment = _swdoc.oCust().subdepartment;
	if(_swdoc.boolInserting)app._etab(_swdoc.MF().Tab1,0);

	if(_swdoc.oCust().sld>0)
	{
		app._een(_swdoc.MF().lbl_userdb_priority, true);
		app._een(_swdoc.MF().fld_userdb_priority, true);
	}

	_swdoc.UpdateFormFromData();	
	_swdoc.ResetModiedFlag("userdb");

	//###20120717
	app._etab(_swdoc.MF().TabServ , 0);

	//--124775: Only show Subscription actions button if you have writes and you are not inserting
	app._evi(_swdoc.MF().mb_serv_action, (app.service.can_manage_cost_and_subs(false) && !_swdoc.boolInserting));

}

function refresh_customer_services()
{
	if(_swdoc.oCust().keysearch!="")
	{
		var serviceKeys = app.service.get_customer_services(_swdoc.oCust().keysearch,true,true);
		var serviceCIKeys = _swdoc.get_customer_ci_services();
		if(serviceKeys=="")
			serviceKeys = "-1";
		serviceKeys = serviceKeys + "," + serviceCIKeys;
			//SG F0112199 - Filter out archived services from list
			var nonArchivedServices = "-1";
			var oRS = app.g.get_sqrecordset("service/process/get_non_archived_services","cids="+serviceKeys);
			while(oRS.Fetch())
			{
				strRelKey = app.g.get_field(oRS,"PK_AUTO_ID");
				nonArchivedServices +=",";
				nonArchivedServices += strRelKey;
			}
			//SG End archived filter
		app._slf(_swdoc.MF().sl_cust_services, "kv="+nonArchivedServices);
	}
}

function refresh_customer_sitelist()
{
	_swdoc.MF().sl_sites.Refresh();
	app.global.Sleep(5);
	var strSites = app.cfg.get_customer_sites(_swdoc.oCust().keysearch, false, "",_swdoc.oCust().fk_cmdb_id);

	var arrSite = strSites.split(",")
	for(var x = 0;x<arrSite.length;x++)
	{
		var strSiteName = arrSite[x];
		var intRow = app.g.sl_findrow_byvalue(_swdoc.MF().sl_sites,strSiteName,'site_name');

		if(intRow>-1)
		{
			_swdoc.MF().sl_sites.SetRowChecked(intRow, true);
		}
	}
}

function get_customer_ci_services()
{
	var strKeys="";
	var strParams = "ks="+app.pfu(_swdoc.oCust().keysearch);
	var aRS = app.g.get_sqrecordset("form/customer/select_customer_services", strParams);
	if (aRS.Fetch())
	{
		var strKey = app.g.get_field(aRS,"fk_ci_id");
		if(strKeys!="")
			strKeys = strKeys + ",";
		strKeys = strKeys + strKey;
	}

	if(strKeys=="")
		strKeys="-1";
	return strKeys;
}

function load_catalogs()
{
	_swdoc.MF().sl_cats.Refresh();
	var strParams = "ks="+app.pfu(_swdoc.oCust().keysearch);
	var aRS = app.g.get_sqrecordset("form/customer/select_kb_notify", strParams);
	while(aRS.Fetch())
	{
		var strKey = app.g.get_field(aRS,"fk_cat_id");
		var intRow = app.g.sl_findrow_byvalue(_swdoc.MF().sl_cats,strKey,1);
		if(intRow>-1)
		{
			_swdoc.MF().sl_cats.SetRowChecked(intRow, true);
		}
	}

}
