OnDataSaved()
{

	//-- if opended form company form - refresh list on company form
    var pDoc = app.g.get_me_document("COMPANY",_swdoc.oCust().fk_company_id);
    if(pDoc)
    {
    	// -- Refresh "Customers" tab in Organisation form
    	app._slf(pDoc.MF().sl_contact , "swc=fk_company_id&kvs=" + encodeURIComponent(_swdoc.oCompany().pk_company_id));
    	pDoc.MF().sl_contact.Refresh();
    }

    //-- 
    //-- NWJ - show tab - and also update parent _swdoc
    if(_swdoc.boolInserting)
    {		   
 		// -- If Customer does not exist, then add relations
 		if(!_swdoc.boolCustomerExists)
 		{
 			var strParams = "ks="+app.pfu(_swdoc.oCust().keysearch);
 			app.g.submitsqs("form/customer/add_cfh_record", strParams);		 
  			//-- need to reload site record to get fk_cmdb_id
   			_swdoc.LoadRecordData("swdata", "userdb", _swdoc.oCust().keysearch);

   			//###20120718
 			if(""!=_swdoc.oCust().fk_company_id){
  				var strParams = "ks="+app.pfu(_swdoc.oCust().keysearch)+"&orgid="+app.pfu(_swdoc.oCust().fk_company_id);
 				app.g.submitsqp("form/customer/add_multi_org_record", strParams);		 
			}
 			//###20120718


    		//-- create app.cmdb relationship
			//-- if customer has a site then relate it as a ci (so can see in vcm company -> site -> customer)
			var strSiteRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultSiteCustRelationship");	
			if(_swdoc.oCust().site != "")    		
			{
				var recSite = app.g.get_record("site", _swdoc.oCust().site)
				app.cmdb.generate_meconfigrelation(recSite.fk_cmdb_id,_swdoc.oCust().fk_cmdb_id,strSiteRel);  
			}
			else
			{
				//-- else form relationship to company (so will see link in vcm company -> customer)
				_swdoc.LoadRecordData("swdata", "company", _swdoc.oCust().fk_company_id);    		

				var strRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultOrgCustRelationship");
   				app.cmdb.generate_meconfigrelation(_swdoc.oCompany().fk_cmdb_id,_swdoc.oCust().fk_cmdb_id,strRel);  
   			}

    		//-- form any other "works at" relationships

    		var strCMDBIDS = app.g.sqllist_getchecked_valuesdel(_swdoc.MF().sl_sites,'fk_cmdb_id',",","", false);
			if(strCMDBIDS!="")
			{
				//-- form relationship is updating (when inserting we build relations once saved
				var arrSiteCMDBIDs = strCMDBIDS.split(",");
				for(var x=0; x <  arrSiteCMDBIDs.length;x++)
				{
					app.cmdb.generate_meconfigrelation(arrSiteCMDBIDs[x],_swdoc.oCust().fk_cmdb_id,strSiteRel,"No");  
				}
			}

 			//-- relate user to kb notifications
			for(var x=0; x <  _swdoc.MF().sl_cats.rowCount();x++)
			{
				if(_swdoc.MF().sl_cats.IsRowChecked(x))
				{
					//--88126
   					var intCatId = _swdoc.MF().sl_cats.GetItemTextRaw(x, 1);	
   					var strCatName = _swdoc.MF().sl_cats.GetItemTextRaw(x, 0);	

					var strParams = "ks="+app.pfu(_swdoc.oCust().keysearch)+"&cid="+intCatId+"&cname="+strCatName;
 					app.g.submitsqs("form/customer/add_kb_notify", strParams);		 
				}
			}

  			if(_swdoc.oCust().subdepartment!="")
			{
				var recSubDept = new Array();
				//-- create ci relationship between sub department and customer
				recSubDept = app.g.get_record("DEPARTMENT", _swdoc.oCust().subdepartment)
				var strDeptRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultDeptCustRelationship");		
				app.cmdb.generate_meconfigrelation(recSubDept.fk_cmdb_id,_swdoc.oCust().fk_cmdb_id,strDeptRel);  		
			}
			else if(_swdoc.oCust().department!="")
			{
				var recDept = new Array();
				//-- create ci relationship between sub department and customer
				recDept = app.g.get_record("DEPARTMENT", _swdoc.oCust().department)
				var strDeptRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultDeptCustRelationship");		
				app.cmdb.generate_meconfigrelation(recDept.fk_cmdb_id,_swdoc.oCust().fk_cmdb_id,strDeptRel);  		
			}	

  			_swdoc.boolInserting=false;
  		}		
	}
	else
	{
		//-- check site change
		if(_swdoc.strCurrentSiteName != _swdoc.oCust().site)
		{
			var recSite = new Array();

			//-- create ci relationship between main site and customer
			recSite = app.g.get_record("SITE", _swdoc.oCust().site)
			var strSiteRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultSiteCustRelationship");		
			app.cmdb.generate_meconfigrelation(recSite.fk_cmdb_id,_swdoc.oCust().fk_cmdb_id,strSiteRel);  		
		}		

		//-- check company change
		if(_swdoc.strCurrentCo != _swdoc.oCust().fk_company_id)
		{
			var recCo = new Array();
			//-- remove the old relationship
			if(_swdoc.strCurrentCo!="")
			{
				recCo = app.g.get_record("COMPANY", _swdoc.strCurrentCo);
				app.cmdb.delete_meconfigrelation(recCo.fk_cmdb_id,_swdoc.oCust().fk_cmdb_id);
			}

			//-- create ci relationship between co and customer if there is not a site
			if(_swdoc.oCust().site=="")
			{
				var strRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultOrgCustRelationship");	
				recCo = app.g.get_record("COMPANY", _swdoc.oCust().fk_company_id)
				app.cmdb.generate_meconfigrelation(recCo.fk_cmdb_id,_swdoc.oCust().fk_cmdb_id,strRel);  		
			}
		}		

		//if sub department has changed
		if(_swdoc.oCust().subdepartment!=_swdoc.strSubDepartment)
		{
			app.cmdb.delete_child_meconfigrelation(_swdoc.oCust().fk_cmdb_id, "ME->DEPARTMENT");
			//if subdeparment is stil set, create relationship to new subdepartment
			if(_swdoc.oCust().subdepartment!="")
			{
				var recSubDept = new Array();
				//-- create ci relationship between sub department and customer
				recSubDept = app.g.get_record("DEPARTMENT", _swdoc.oCust().subdepartment)
				var strDeptRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultDeptCustRelationship");		
				app.cmdb.generate_meconfigrelation(recSubDept.fk_cmdb_id,_swdoc.oCust().fk_cmdb_id,strDeptRel);  
			}
			else if(_swdoc.oCust().department!="")
			{
				var recDept = new Array();
				//-- create ci relationship between sub department and customer
				recDept = app.g.get_record("DEPARTMENT", _swdoc.oCust().department)
				var strDeptRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultDeptCustRelationship");		
				app.cmdb.generate_meconfigrelation(recDept.fk_cmdb_id,_swdoc.oCust().fk_cmdb_id,strDeptRel);  		
			}
		}
		else if(_swdoc.oCust().department!=_swdoc.strDepartment)
		{
			app.cmdb.delete_child_meconfigrelation(_swdoc.oCust().fk_cmdb_id, "ME->DEPARTMENT");
			//if department has changed, and dept is still set to something
			if(_swdoc.oCust().department!="")
			{
				var recDept = new Array();
				//-- create ci relationship between sub department and customer
				recDept = app.g.get_record("DEPARTMENT", _swdoc.oCust().department)
				var strDeptRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultDeptCustRelationship");		
				app.cmdb.generate_meconfigrelation(recDept.fk_cmdb_id,_swdoc.oCust().fk_cmdb_id,strDeptRel);  		
			}
		}	

	}
	_swdoc.strDepartment = _swdoc.oCust().department;
	_swdoc.strSubDepartment = _swdoc.oCust().subdepartment;

	_swdoc.setup_form_fields()

}

OnSaveData()
{
	//-- store company name
	_swdoc.oCust().companyname = _swdoc.MF().lb_company1.display;

	// -- Check if the Customer already exists
	var strCheckParams = "ks="+_swdoc.oCust().keysearch;
	var oRS = app.g.get_sqrecordset("form/customer/select_check_customer_exists",strCheckParams);
	var intKS_CNT = 0;
	while(oRS.Fetch()) intKS_CNT = app.g.get_field(oRS,"KS_CNT");
	if(intKS_CNT!=0) _swdoc.boolCustomerExists = true;

	//RJC ITSM FR10
	if(_swdoc.cfh_ra_update)
	{   		
		//Copy the existing RA record
		var newRAKey = app.g.copy_sw_record("cfh_ra", cfh_ra, "", new Array());

		var arrExisting = new Array;
		var strNewKeys = "";
		var strExisting = app.g.sqllist_get_valuesdel(mainform.sl_ra_roles, 2, ",", "");
		//if there are roles associated with the RA
		if(strExisting!="")
		{
			arrExisting = strExisting.split(",");
			for(var i =0;i<arrExisting.length;i++)
			{
				//foreach role, copy the record
   				var oRec = app.g.get_record("cfh_ra_role", arrExisting[i]);
				var newValue 		= app.g.copy_sw_record("cfh_ra_role", oRec, "");//app.g.get_field(oRS,"role_id");
				if(strNewKeys!="")strNewKeys+=",";	
				strNewKeys += newValue;			
			}

			//update the new records with the new RA id
			var strParams = "rid="+newRAKey+"&keys="+strNewKeys;
			app.g.submitsqs("form/customer/update_cfh_roles", strParams);
		}
		//update the customer record with the new RA key
		_swdoc.oCust().cfh_ra=newRAKey;
		//reload the RA information
		var strParams = "rid="+newRAKey+"&aid="+app.pfu(app.session.analystId);
		app.g.submitsqs("form/customer/update_cfh_ra", strParams);

		_swdoc.LoadRecordData("swdata","cfh_ra", newRAKey);
		_swdoc.cfh_ra_update = false;
	}

	return true;
}

